	sFORMLOCATION = "";

	// È­¸é±×¸®µå ¸®»çÀÌÁî
	//fcSetResize(this,this.width);	

	//È­¸é Á¤º¸ Display ¹× Button ±ÇÇÑ ¼³Á¤
