function fcSearchCheck(obj) {
    //Á¶È¸Á¶°Ç Ã¼Å©¹× trimÃ³¸®

	if (chkMeetCd001.Value == "N" && chkMeetCd002.Value == "N" && 
	    chkMeetCd003.Value == "N" && chkMeetCd004.Value == "N") {
		return false;
	}

	fcInit(obj);    //±âÁ¸ µ¥ÀÌÅÍ¼Â ÃÊ±âÈ­

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
var strCommand = "";
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    if (chkDetailAll.Value == "1") {
		//¼¼ºÎ³»¿ª Á¶È¸ÀÎ °æ¿ì
		fcd_AddParam("COMM_NO", 	cboBrncCd.Value); 			// ÁöÁ¡ÄÚµå(¼¼ºÎÀÎ °æ¿ì¿¡¸¸ º¸³»¾ß ÇÔ)
		searchTelmpTellerView(null);
		return;
    } else {
		chkDetailAll.value = "0";
		chkDetailAll.Visible = false;
    }

	var sServiceName = "rsm3120-service:searchTelmpTeller01";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTelmpTeller=dsTelmpTeller ";

    strCommand = "";    

    //fcd_AddParam("OPTION", 		chkOption.Value);			// °æ·û±¸ºÐÄÚµå	
    //fcd_AddParam("MEET_CD", 	rdoMeetCd.Value);			// °æ·û±¸ºÐÄÚµå	
    fcd_AddParam("MEET_CD_001", chkMeetCd001.Value);		// ±¤¸í°æ·û
    fcd_AddParam("MEET_CD_002", chkMeetCd002.Value);		// Ã¢¿ø°æ·û
    fcd_AddParam("MEET_CD_003", chkMeetCd003.Value);		// °æÁ¤(¹Ì»ç¸®)
    fcd_AddParam("MEET_CD_004", chkMeetCd004.Value);		// ºÎ»ê°æ·û
    fcd_AddParam("TABLE_NM", 	cboTableNm.Value ); 		// °æÁÖ±Ç, ±¸¸Å±Ç
    fcd_AddParam("TYPE", 		cboType.Value );			// ¹ß¸Å¿øº°, Ã¢±¸º°
	fcd_AddParam("RACE_DAY_ST", calRaceDaySt.Value);		// °Ë»ö ½ÃÀÛÀÏ
	fcd_AddParam("RACE_DAY_END",calRaceDayEnd.Value);		// °Ë»ö Á¾·áÀÏ	
	fcd_AddParam("COMM_NO", 	cboBrncCd.Value); 			// °æÁÖ±Ç, ±¸¸Å±Ç

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);	

	if (cboType.Value == "TELLER_ID") 	Static7.text = "°³ÀÎº° ¹ß¸Å";
	else 								Static7.text = "Ã¢±¸º° ¹ß¸Å";

}

function fcTelmpTellerOnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	var TELLER_ID	=	grdTelmpTeller.GetCellValue(nRow,1);

	if(strCommand == "" && nCell==1)
	{
		searchTelmpTellerView(TELLER_ID);
		chkDetailAll.Visible = true;
	}

}

function searchTelmpTellerView(TellerId)
{
	var sServiceName = "rsm3120-service:searchTelmpTellerView01";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTelmpTeller=dsTelmpTeller ";

    fcd_AddParam("MODE", "VIEW");	// °æ·û±¸ºÐÄÚµå	
    //fcd_AddParam("MEET_CD", rdoMeetCd.Value);	// °æ·û±¸ºÐÄÚµå	
    //fcd_AddParam("OPTION", chkOption.Value);	// ±³Â÷Æ÷ÇÔ	
    fcd_AddParam("MEET_CD_001", chkMeetCd001.Value);		// ±¤¸í°æ·û
    fcd_AddParam("MEET_CD_002", chkMeetCd002.Value);		// Ã¢¿ø°æ·û
    fcd_AddParam("MEET_CD_003", chkMeetCd003.Value);		// °æÁ¤(¹Ì»ç¸®)
    fcd_AddParam("MEET_CD_004", chkMeetCd004.Value);		// ºÎ»ê°æ·û
    fcd_AddParam("TABLE_NM", cboTableNm.Value ); // °æÁÖ±Ç, ±¸¸Å±Ç
    fcd_AddParam("TYPE", cboType.Value );		// ¹ß¸Å¿øº°, Ã¢±¸º°
	fcd_AddParam("RACE_DAY_ST", calRaceDaySt.Value);	// °Ë»ö ½ÃÀÛÀÏ
	fcd_AddParam("RACE_DAY_END", calRaceDayEnd.Value);	// °Ë»ö Á¾·áÀÏ	
	fcd_AddParam("TELLER_ID", TellerId);	// °Ë»ö Á¾·áÀÏ	

	strCommand = "VIEW";
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


