/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if ( sServiceName = "common-service:searchCode") {
		cboBrncCd.value = gDsUser.GetColumn(0, "BRNC_CD");
		// var v_row = dsBrncCd.SearchRow("CD='01'");
		// if (v_row > -1) {
			// dsBrncCd.DeleteRow(v_row);
		// }
    } else if ( sServiceName == "rsm3120-service:searchTelmpTeller01" ) {

	}	
	//Ã¢±¸º°ÀÎ °æ¿ì 
	if (cboType.Value == "WIN_NO" && strCommand != "VIEW") {
		grdTelmpTeller.SetColProp(2,"Width","0");
		grdTelmpTeller.SetColProp(3,"Width","0");
		grdTelmpTeller.SetColProp(4,"Width","0");		
	} else {
		grdTelmpTeller.SetColProp(2,"Width","56");
		grdTelmpTeller.SetColProp(3,"Width","56");
		grdTelmpTeller.SetColProp(4,"Width","58");
	}

	if(strCommand == "VIEW")
	{	
		grdTelmpTeller.SetCellProp("head",1,"text","¹ß¸ÅÀÏÀÚ");
		grdTelmpTeller.SetCellProp("body",1,"colid","RACE_DAY");
		grdTelmpTeller.SetCellProp("body",1,"font","±¼¸²,9");
		grdTelmpTeller.SetCellProp("body",1,"color","default");
		grdTelmpTeller.SetCellProp("body",1,"cursor","arrow");
	}
	else
	{
		if (cboType.Value == "WIN_NO") {
			grdTelmpTeller.SetCellProp("head",1,"text","Ã¢±¸¹øÈ£");
		} else {
			grdTelmpTeller.SetCellProp("head",1,"text","¹ß¸Å¿øID");		
		}
		//grdTelmpTeller.SetCellProp("head",1,"text","¹ß¸Å¹øÈ£");
		grdTelmpTeller.SetCellProp("body",1,"colid","TELLER_ID");
		grdTelmpTeller.SetCellProp("body",1,"font","±¼¸²,9,Bold");
		grdTelmpTeller.SetCellProp("body",1,"color","#10A603");
		grdTelmpTeller.SetCellProp("body",1,"cursor","hand");
	}

	//grdTelmpTeller.SetFocus();
}


/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio ¹Ù²ð ¶§ ÀÌº¥Æ®
+-------------------------------------------------------------------------------------------------*/

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.id ) {
		case "rdoMeetCd" :
			if(strCode != "001")
			{
				chkOption.Enable = false;
				chkOption.Value = 'N';
			}
			else
			{
				chkOption.Enable = true;
			}

			break;
	}

	fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  checkbox Å¬¸¯ÇÒ ¶§ ÀÌº¥Æ®
+-------------------------------------------------------------------------------------------------*/

function fcOptionOnClick(obj,strValue)
{
	fcSearch();
}

function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// °æÁÖ °Ë»ö ½ÃÀÛ ÀÏÀÚ¿¡¼­ ENTER_KEY ´­·¶À» °æ¿ì
		case "calRaceDaySt" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;
		// °æÁÖ °Ë»ö ¸¶Áö¸· ÀÏÀÚ¿¡¼­ ENTER_KEY ´­·¶À» °æ¿ì
		case "calRaceDayEnd" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;

	}
}



// Æ¯Á¤ÄÞÆ÷³ÍÆ®(±×¸®µå) ¸®»çÀÌÁî
function fcSetResize(obj,nX)
{	
	var arrObj = array(-1);
	arrObj[0] = "grdTelmpTeller";

	var nWidth = nX-8;
	if( nX <= 820){
		for( var i=0; i<length(arrObj); i++){
			object(arrObj[i]).Autofit = false;		
			object(arrObj[i]).width = 801;
			object(arrObj[i]).Redraw;
		}
		Shape1.Width 	= 801;	
		divBtnLst.Left 	= this.width - 393;			
		Static0.Left 	= this.width - 91;
		return;
	}

	for( var i=0; i<length(arrObj); i++){
		object(arrObj[i]).right = nWidth;
		object(arrObj[i]).Autofit = true;		
	}	

	// °øÅë
	Shape1.Width 	= nWidth-8;
	divBtnLst.Left 	= this.width - 393;	
	Static0.Left	= this.width - 91;
}

