function fcInit(obj) {
    dsTelmpTeller.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리

	if (chkMeetCd001.Value == "N" && chkMeetCd002.Value == "N" && 
	    chkMeetCd003.Value == "N" && chkMeetCd004.Value == "N") {
		return false;
	}

	fcInit(obj);    //기존 데이터셋 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
var strCommand = "";
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    if (chkDetailAll.Value == "1") {
		//세부내역 조회인 경우
		fcd_AddParam("COMM_NO", 	cboBrncCd.Value); 			// 지점코드(세부인 경우에만 보내야 함)
		searchTelmpTellerView(null);
		return;
    } else {
		chkDetailAll.value = "0";
		chkDetailAll.Visible = false;
    }

	var sServiceName = "rsm3120-service:searchTelmpTeller01";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTelmpTeller=dsTelmpTeller ";

    strCommand = "";    

    //fcd_AddParam("OPTION", 		chkOption.Value);			// 경륜구분코드	
    //fcd_AddParam("MEET_CD", 	rdoMeetCd.Value);			// 경륜구분코드	
    fcd_AddParam("MEET_CD_001", chkMeetCd001.Value);		// 광명경륜
    fcd_AddParam("MEET_CD_002", chkMeetCd002.Value);		// 창원경륜
    fcd_AddParam("MEET_CD_003", chkMeetCd003.Value);		// 경정(미사리)
    fcd_AddParam("MEET_CD_004", chkMeetCd004.Value);		// 부산경륜
    fcd_AddParam("TABLE_NM", 	cboTableNm.Value ); 		// 경주권, 구매권
    fcd_AddParam("TYPE", 		cboType.Value );			// 발매원별, 창구별
	fcd_AddParam("RACE_DAY_ST", calRaceDaySt.Value);		// 검색 시작일
	fcd_AddParam("RACE_DAY_END",calRaceDayEnd.Value);		// 검색 종료일	
	fcd_AddParam("COMM_NO", 	cboBrncCd.Value); 			// 경주권, 구매권

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);	

	if (cboType.Value == "TELLER_ID") 	Static7.text = "개인별 발매";
	else 								Static7.text = "창구별 발매";

}

function fcTelmpTellerOnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	var TELLER_ID	=	grdTelmpTeller.GetCellValue(nRow,1);

	if(strCommand == "" && nCell==1)
	{
		searchTelmpTellerView(TELLER_ID);
		chkDetailAll.Visible = true;
	}

}

function searchTelmpTellerView(TellerId)
{
	var sServiceName = "rsm3120-service:searchTelmpTellerView01";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTelmpTeller=dsTelmpTeller ";

    fcd_AddParam("MODE", "VIEW");	// 경륜구분코드	
    //fcd_AddParam("MEET_CD", rdoMeetCd.Value);	// 경륜구분코드	
    //fcd_AddParam("OPTION", chkOption.Value);	// 교차포함	
    fcd_AddParam("MEET_CD_001", chkMeetCd001.Value);		// 광명경륜
    fcd_AddParam("MEET_CD_002", chkMeetCd002.Value);		// 창원경륜
    fcd_AddParam("MEET_CD_003", chkMeetCd003.Value);		// 경정(미사리)
    fcd_AddParam("MEET_CD_004", chkMeetCd004.Value);		// 부산경륜
    fcd_AddParam("TABLE_NM", cboTableNm.Value ); // 경주권, 구매권
    fcd_AddParam("TYPE", cboType.Value );		// 발매원별, 창구별
	fcd_AddParam("RACE_DAY_ST", calRaceDaySt.Value);	// 검색 시작일
	fcd_AddParam("RACE_DAY_END", calRaceDayEnd.Value);	// 검색 종료일	
	fcd_AddParam("TELLER_ID", TellerId);	// 검색 종료일	

	strCommand = "VIEW";
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


