    //조회조건 체크및 trim처리

	fcInit(obj);    //기존 데이터셋 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;
    trace("#####tab0.TabIndex:" + tab0.TabIndex);
	var sServiceName = "rsm3200-service:searchDivSales"; // 투표소별 발매(회차별,전체지점) 
    var sInDataSet   = "";
    var sOutDataSet  = "dsSellst=dsSellst ";
/*
    if (tab0.TabIndex == 1) {
		sServiceName = "rsm3200-service:searchDivSalesRefine"; // 투표소별 발매(회차별,전체지점) 
		sInDataSet   = "";
		sOutDataSet  = "dsSellstRefine=dsSellstRefine ";
    } else 
*/     
    if (tab0.TabIndex == 1) {
		sServiceName = "rsm3200-service:searchDivSalesRefineWin"; // 투표소/창구별 발매(회차별,전체지점) 
		sInDataSet   = "";
		sOutDataSet  = "dsSellstRefineWin=dsSellstRefineWin ";
    } else if (tab0.TabIndex == 2) {
		sServiceName = "rsm3200-service:searchDivSalesRefineGrnZn"; //  그린존별 발매건수
		sInDataSet   = "";
		sOutDataSet  = "dsSellstRefineGrnZn=dsSellstRefineGrnZn ";
    	if (!fnc_isNull(cboTms.Value)) {
			fcd_AddParam("MEET_CD", 	dsTmsList.GetColumn(cboTms.Index, "MEET_CD")); 	//  경륜/경정 구분
			fcd_AddParam("STND_YEAR", 	dsTmsList.GetColumn(cboTms.Index, "STND_YEAR")); 	// 경주년도
			fcd_AddParam("TMS", 	    dsTmsList.GetColumn(cboTms.Index, "TMS")); 		// 회차	
		}
    } 

    fcd_AddParam("JOB_TYPE", tab0.TabIndex);		//0:전체,1:현금, 2:그린카드
    fcd_AddParam("CHK_REFUND", chkRefund.Value);	// 환불액 포함여부
    fcd_AddParam("CHK_GREEN", chkGreen.Value);		// 그린카드 포함여부    

	fcd_AddParam("MEET_CD_001", chkMeetCd001.Value);		// 광명경륜
    fcd_AddParam("MEET_CD_002", chkMeetCd002.Value);		// 창원경륜
    fcd_AddParam("MEET_CD_003", chkMeetCd003.Value);		// 경정(미사리)
    fcd_AddParam("MEET_CD_004", chkMeetCd004.Value);		// 부산경륜

	fcd_AddParam("RACE_DAY_ST", calRaceDaySt.Value);	// 검색 시작일
	fcd_AddParam("RACE_DAY_END", calRaceDayEnd.Value);	// 검색 종료일
	fcd_AddParam("COMM_NO", 	cboBrncCd.Value); // 지점코드


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

	stInfo.Text = "";
	if (tab0.TabIndex <> "0") {
		if (cboBrncCd.Value != "00") {
			stInfo.Text = "※ 그린카드 매출액은 지점별 분리가 불가하여 포함되어 있지 않습니다.";
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  회차조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchTms(obj) {

	var sServiceName = "rsm3200-service:searchTmsList"; 
    var sInDataSet   = "";
    var sOutDataSet  = "dsTmsList=dsTmsList ";

    if (chkMeetCd001.Value == "Y") {
		fcd_AddParam("MEET_CD", "001");	//경륜/경정 구분
    } else if (chkMeetCd003.Value == "Y") {
		fcd_AddParam("MEET_CD", "003");	//경륜/경정 구분
    } else {
		dsTmsList.ClearData();
		return;
    }
    fcd_AddParam("STND_YEAR", Mid(calRaceDaySt.Value,0,4));	//경주년도

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


