function fcToExcel(obj) {
    var sExcelList = "tab0.tab1.grdSellst:투표소별 매출액"; //데이터셋 정의
    if (tab0.TabIndex == 1) {
		sExcelList = "tab0.tab2.grdSellst:투표소별 매출액"; //데이터셋 정의
    } else if (tab0.TabIndex == 2) {
		sExcelList = "tab0.tab3.grdSellst:투표소별 매출액"; //데이터셋 정의
    } else if (tab0.TabIndex == 3) {
		sExcelList = "tab0.tab4.grdSellst:그린존별 발매건수"; //데이터셋 정의
    } 
    fcd_OpenExcelListExport(sExcelList);
}

