/*-------------------------------------------------------------------------------------------------+
>>  인쇄
+-------------------------------------------------------------------------------------------------*/
function fcPrint(){
	ds_reportParam.DeleteAll();
	var nRnum;	
	var sTitle    = "";
	var sMeetCd   = "";
	var sStndYear = "";
	var sTms      = "";

	if (!fnc_isNull(cboTms.Value)) {
		sTitle    = cboTms.Text;
		sMeetCd   = dsTmsList.GetColumn(dsTmsList.row, "MEET_CD");
		sStndYear = dsTmsList.GetColumn(dsTmsList.row, "STND_YEAR");
		sTms      = dsTmsList.GetColumn(dsTmsList.row, "TMS");
	}

	// 필드 값을 셋팅한다.
	var paramField = [
							"MEET_CD_001"						//  광명경륜 여부
						,	"MEET_CD_002"						// 창원경륜 여부
						,	"MEET_CD_003"						// 미사리경정 여부
						,	"MEET_CD_004"						// 부산경륜 여부
						,	"RACE_DAY_ST"						// 
						,	"RACE_DAY_END"						// 
						,	"COMM_NO"						// 
						,	"CHK_GREEN"						// 
						,	"TITLE"						// 
						,   "MEET_CD"
						,   "STND_YEAR"
						,   "TMS"

					 ];

	// 값을 셋팅한다.
	var paramValue = [
							chkMeetCd001.Value
						,	chkMeetCd002.Value
						,	chkMeetCd003.Value
						,	chkMeetCd004.Value
						,	calRaceDaySt.Value
						,	calRaceDayEnd.Value
						,	cboBrncCd.Value
						,	chkGreen.Value
						,	sTitle + " 그린존별 발매건수"
						,   sMeetCd
						,   sStndYear
						,   sTms
					 ];


	var rowCnt = paramField.length;	
	for(var row = 0 ; row < rowCnt; row++)
	{
		nRnum = ds_reportParam.AppendRow();
		ds_reportParam.SetColumn(nRnum, "PARAM_NM", paramField[row]);
		ds_reportParam.SetColumn(nRnum, "PARAM_VAL",paramValue[row]);
	}

	/* 레포트 배율 추가 - 시작 */
	iRow = ds_reportParam.AddRow();

	// Default Scale 파라미터명 추가(고정)
	ds_reportParam.SetColumn(iRow, "PARAM_NM", "DEFAULT_SCALE");
	ds_reportParam.SetColumn(iRow, "PARAM_VAL", "1");

	//레포트 트랙잭션을 태운다.
	fnc_ReportPreView("rsm3200_4", ds_reportParam.ID, "Y");		
}

