/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if ( sServiceName == "common-service:searchCode" ) {
		var v_row = dsBrncCd.SearchRow("CD='06'");
		if (v_row > -1) {
			dsBrncCd.DeleteRow(v_row);
		}		
		 v_row = dsBrncCd.SearchRow("CD='29'");//교차
		if (v_row > -1) {
			dsBrncCd.DeleteRow(v_row);
		}		
		cboBrncCd.value = gDsUser.GetColumn(0, "BRNC_CD");

   	} else if (sServicename == "rsm3200-service:searchDivSalesRefineWin") {
		if (dsSellstRefineWin.rowcount()>0) {
			var aTerminal = Array(4);
			for (var icnt=0;icnt<Length(aTerminal);icnt++) aTerminal[icnt]=0;
			for (var iRow=0;iRow<dsSellstRefineWin.RowCount();iRow++) {
				switch(dsSellstRefineWin.GetColumn(iRow, "WIN_TYPE")) {
					case "1":	// 유인
						aTerminal[0] ++;
						break;
					case "3":	// 무인
						aTerminal[1] ++;
						break;
					case "11":	// ???
						aTerminal[2] ++;
						break;
					case "14":	// 그린카드
						aTerminal[3] ++;
						break;						
				}				
			}

			// 그리드에 표시
		}
   	}

}

/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio 바뀔 때 이벤트
+-------------------------------------------------------------------------------------------------*/

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "cboBrncCd") {
		if (cboBrncCd.value == "00") {
			chkGreen.Enable = true;
		} else {
			if (tab0.tabindex != 0) {
				chkGreen.value = false;
				chkGreen.Enable = false;
			}
		}
		fcSearch();
	}
}

function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// 경주 검색 시작 일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDaySt" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;
		// 경주 검색 마지막 일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDayEnd" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;

	}
}


// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX,nY)
{	
	return;
	if( nY <= 576){
		grdSellst.Height = 449;
	} else {
		grdSellst.Height = nY - grdSellst.top;
	}
}

// 해당 폼사이즈 변경시
function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx,nCy);
}
function Tab0_OnChanged(obj,nOldIndex,nNewindex)
{
	fcSearch(obj);
	if (nNewIndex == 0) stInfo.Visible = true;
	else                stInfo.Visible = false; 
}

function fcTabOnChanged(obj,nOldIndex,nNewindex)
{

	if (obj.id == "Tab0") {
		if (nOldIndex == 0) {
			orgChkRefund = chkRefund.Value;		
			orgChkGreen  = chkGreen.Value;		
		}
		if (nNewindex == 0) {			
			chkRefund.Value = orgChkRefund;
			chkRefund.Enable = true;
			chkGreen.value = orgChkGreen;
			chkGreen.Enable = true;
		} else {
			chkRefund.Value = true;
			chkRefund.Enable = false;
			chkGreen.Value = false;
			chkGreen.Enable = false;
		}
		if (nNewindex == 3) {
			cboTms.Visible = true;
		} else {
			cboTms.Visible = false;
		}

	}
}

function fcCalOnChanged(obj,strOldText,strNewText)
{
	fcSearchTms(obj);
}

function fcChkOnClick(obj,strValue)
{
	fcSearchTms(obj);
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var bEnable = fnc_Isnull(cboTms.Value);
	stTerm.Enable = bEnable;
	calRaceDaySt.Enable = bEnable;
	calRaceDayEnd.Enable = bEnable;
}
