/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

var ARRAY2_GBL_RET_VALUE = Array();	// 이전페이지에서 넘어온 변수
var RACE_DAY = "";
function fcFormLoadSetting(obj) {  

    sBUTTONLIST   = 'TTFFTFT';     // T초기화,T조회,F저장,F삭제,T엑셀,F인쇄,T닫기

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	// 이전 페이지에서 파라미터 받음
	ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE," ","=");
	GBL_RET_VALUE="";


    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 기준년도	
	fcd_SetStndYear(dsStndYear,  dsCond.GetColumn(0,"MIN_STND_YEAR"), substr(fcd_GetSysdate("YYYYMMDD"), 0, 4)); 

	var STND_YEAR = fcArray2Value(ARRAY2_GBL_RET_VALUE,"STND_YEAR");
	var MEET_CD = fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD");
	cboStndYear.Value = STND_YEAR;

    RACE_DAY		= fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_DAY");
	fcSearchInfo();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsSalesAMT|dsExcTAX|dsProfit|dsRefund|dsBrncEntPrsnNum";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}



/*-------------------------------------------------------------------------------------------------+
>>  화면 실행할 때마다 실행
+-------------------------------------------------------------------------------------------------*/
function fcFormOnActivite(){
	var sRetValue = decode(GBL_RET_VALUE,null,'',GBL_RET_VALUE);
	if(sRetValue!='')
	{		
		// 이전 페이지에서 파라미터 받음	
		ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE," ","=");
		GBL_RET_VALUE="";
		cboStndYear.Value  = fcArray2Value(ARRAY2_GBL_RET_VALUE,"STND_YEAR");
		RACE_DAY		= fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_DAY");	
		fcSearchInfo();
	}
}




/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == ENTER_KEY) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

}

