    //조회조건 체크및 trim처리

	fcInit(obj);    //기존 데이터셋 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    var strParam = "STND_YEAR="+cboStndYear.Value;

	GBL_RET_VALUE=strParam; // 값 생성	

	global.FrameBottom.fcOpenMdiForm("RSM4010", "일일발매실적", "frm_rsm::RSM4010.xml");

}


function fcSearchInfo(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm4011-service:searchRaceInfo"; // 경륜 조회

    var sInDataSet   = "";
    var sOutDataSet  = "dsRaceInfo=dsRaceInfo ";
		sOutDataSet  += "dsSalesAMT=dsSalesAMT ";
		sOutDataSet  += "dsExcTAX=dsExcTAX ";
		sOutDataSet  += "dsProfit=dsProfit ";
		sOutDataSet  += "dsRefund=dsRefund ";
		sOutDataSet  += "dsBrncEntPrsnNum=dsBrncEntPrsnNum ";

	var MEET_CD		= fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD");		

    // TMS ,DAY_ORD	전페이지 파라미터
	fcd_AddParam("MEET_CD", MEET_CD);	// 경륜 구분 코드
	fcd_AddParam("STND_YEAR", cboStndYear.Value);	// 기준년도
	fcd_AddParam("RACE_DAY", RACE_DAY);						// 회차

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

// 콤보 박스
function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}

