function fcToExcel(obj) {

	var sExcelList = "grdSalesAMT:2"; 
		sExcelList += "|grdExcTAX:1"; 
		sExcelList += "|grdProfit:1"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
		sExcelList += "|grdRefund:1"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
		sExcelList += "|grdBrncEntPrsnNum:3"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격		    

		fnc_OpenMultiExcelExport(sExcelList,"발매일일실적보고서("+RACE_DAY+")");

}

