/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "rsm4041-service:searchRaceInfo" ) 
	{		
    }

    var MEET_CD		= fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD");		    
    var MEET_NM	= decode(MEET_CD,'003','경정','경륜');
    var RACE_DAY = dsRaceInfo.GetColumn(0,"RACE_DAY");
    var RACE_YOIL_KOR= dsRaceInfo.GetColumn(0,"RACE_YOIL_KOR");
    var TMS= dsRaceInfo.GetColumn(0,"TMS");
    var DAY_ORD= dsRaceInfo.GetColumn(0,"DAY_ORD");
	stRaceInfo.Text = RACE_DAY+"("+RACE_YOIL_KOR+") "+MEET_NM+" "+TMS+"회차 "+DAY_ORD+"일차";


	// 지점별 매출액 총계
	var TOT = dsSalesAMT.GetColumn(0,"TOT");
	var PT_TOT = dsSalesAMT.GetColumn(0,"PT_TOT");
	grdBrncEntPrsnNum.SetCellProp("summary","1","text",TOT);
	grdBrncEntPrsnNum.SetCellProp("summary","2","text",PT_TOT);
}

