/*-------------------------------------------------------------------------------------------------+
>>  함수 정의
+-------------------------------------------------------------------------------------------------*/

// 2차원 배열의 변수 값 확인
function fcArray2Value(arr2Param,strParam)
{
	var strResult = "";
	for(var i=0;i<arr2Param.length;i++)
	{	
		for(var j=0;j<arr2Param[i].length;j++)
		{
			if(arr2Param[i][0]==strParam)
			{
				strResult = arr2Param[i][1];
			}
		}
	}
	return strResult;
}


/*-------------------------------------------------------------------------------------------------+
>>  기타소득세 보기 버튼 클릭 시 발생 이벤트
+-------------------------------------------------------------------------------------------------*/
function fcBtnOnClick(obj,nX,nY,nRow)
{	
	var MEET_CD = fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD");	// 경륜 경정 구분 코드 
	var RACE_YY = substr(dsRaceInfo.GetColumn(nRow,"RACE_DAY"), 0, 4);
	var RACE_MM = substr(dsRaceInfo.GetColumn(nRow,"RACE_DAY"), 5, 2);
	var RACE_DD = substr(dsRaceInfo.GetColumn(nRow,"RACE_DAY"), 8, 2);
	var RACE_DAY = RACE_YY + RACE_MM + RACE_DD;
	var STND_YEAR = dsRaceInfo.GetColumn(nRow,"STND_YEAR");

    var MEET_NM	= decode(MEET_CD,'003','경정','경륜');
    var RACE_DAY1 = dsRaceInfo.GetColumn(0,"RACE_DAY");
    var RACE_YOIL_KOR= dsRaceInfo.GetColumn(0,"RACE_YOIL_KOR");
    var TMS= dsRaceInfo.GetColumn(0,"TMS");
    var DAY_ORD= dsRaceInfo.GetColumn(0,"DAY_ORD");

    var MEET_CD_ETC = fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD_ETC");	// 교차 구분 코드
    var TMS_ETC = fcArray2Value(ARRAY2_GBL_RET_VALUE,"TMS_ETC");	// 교차 경기 회차
    var DAY_ORD_ETC = fcArray2Value(ARRAY2_GBL_RET_VALUE,"DAY_ORD_ETC");	// 교차 경기 정보

	var sParam = " MEET_CD="+MEET_CD;
	    sParam += " STND_YEAR="+STND_YEAR;
		sParam += " RACE_DAY="+RACE_DAY;
		sParam += " MEET_NM="+MEET_NM;
		sParam += " RACE_DAY1="+RACE_DAY1;
		sParam += " RACE_YOIL_KOR="+RACE_YOIL_KOR;
		sParam += " TMS="+TMS;
		sParam += " DAY_ORD="+DAY_ORD;
		sParam += " MEET_CD_ETC="+MEET_CD_ETC;
		sParam += " TMS_ETC="+TMS_ETC;
		sParam += " DAY_ORD_ETC="+DAY_ORD_ETC;

	GBL_RET_VALUE=sParam; // 값 생성	
	global.FrameBottom.fcOpenMdiForm("RSM4012", "일일발매실적-기타소득세", "frm_rsm::RSM4012.xml");
}
