/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsGitaBonbu.ClearData();
    dsGitaChangwon.ClearData();
    dsGitaBusan.ClearData();
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리

	fcInit(obj);    //기존 데이터셋 초기화

	return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    var strParam = "STND_YEAR="+cboStndYear.Value;

	GBL_RET_VALUE=strParam; // 값 생성	

	global.FrameBottom.fcOpenMdiForm("RSM4010", "일일발매실적", "frm_rsm::RSM4010.xml");

}


function fcSearchInfo(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var MEET_CD_ETC = fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD_ETC");	// 교차 구분 코드
    var MEET_CD		= fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD");		
	var MEET_CD1 = '099';

    var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	if(MEET_CD == '001'){
		sServiceName = "rsm4012-service:searchGitaBonbu"; // 기타소득세 조회 경륜
		sOutDataSet  = "dsGitaBonbu=dsGitaBonbu ";
		sOutDataSet  += "dsGitaChangwon=dsGitaChangwon ";
		sOutDataSet  += "dsGitaBusan=dsGitaBusan ";
	}else if(MEET_CD == '003'){
		sServiceName = "rsm4012-service:searchGitaBonbuBoat"; // 기타소득세 조회 경정
		sOutDataSet  = "dsGitaBonbu=dsGitaBonbu ";
		sOutDataSet  += "dsGitaChangwon=dsGitaChangwon ";
		sOutDataSet  += "dsGitaBusan=dsGitaBusan ";
	} 

	// TMS ,DAY_ORD	전페이지 파라미터

	fcd_AddParam("MEET_CD", MEET_CD);				// 경륜 구분 코드
	fcd_AddParam("MEET_CD1", MEET_CD1);				// 경륜 구분 코드
	fcd_AddParam("MEET_CD_ETC", MEET_CD_ETC);		// 교차 구분 코드
	fcd_AddParam("STND_YEAR", cboStndYear.Value);	// 기준년도
	fcd_AddParam("RACE_DAY", RACE_DAY);				// 회차

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

// 콤보 박스
function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {

	var sExcelList = "grdGita:1"; 
	sExcelList += "|Grid0"; 
	sExcelList += "|Grid1:1"; 

	fnc_OpenMultiExcelExport(sExcelList,"발매일일실적-기타소득세("+RACE_DAY+")");

}


