function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "rsm4012-service:searchGitaBonbu" ) 
	{

    }else if ( sServiceName == "rsm4012-service:searchGitaBonbu" ){

	}else if ( sServiceName == "rsm4012-service:searchGitaBonbu" ){

    }

    var MEET_NM	= fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_NM");
    var RACE_DAY1 = fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_DAY1");
    var RACE_YOIL_KOR= fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_YOIL_KOR");
    var TMS= fcArray2Value(ARRAY2_GBL_RET_VALUE,"TMS");
    var DAY_ORD= fcArray2Value(ARRAY2_GBL_RET_VALUE,"DAY_ORD");
    var MEET_CD_ETC = fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD_ETC");	// 교차 구분 코드

    stRaceInfo.Text = RACE_DAY1+"("+RACE_YOIL_KOR+") "+MEET_NM+" "+TMS+"회차 "+DAY_ORD+"일차";

    if(MEET_CD_ETC != null){

		//var MEET_CD_ETC = fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD_ETC");	// 교차 구분 코드
		var MEET_ETC_NM = decode(MEET_CD_ETC, '002', '창원', '004', '부산');
		var TMS_ETC = fcArray2Value(ARRAY2_GBL_RET_VALUE,"TMS_ETC");	// 교차 경기 회차
		var DAY_ORD_ETC = fcArray2Value(ARRAY2_GBL_RET_VALUE,"DAY_ORD_ETC");	// 교차 경기 정보

		//stRaceInfoGyocha.Text = RACE_DAY1+"("+RACE_YOIL_KOR+") "+MEET_ETC_NM+" "+TMS_ETC+"회차 "+DAY_ORD_ETC+"일차";
    }else{
		//stRaceInfoGyocha.Text = "";
    }

    var GITA1_BONBU = dsGitaBonbu.GetColumn(0, "GITA1");
    var GITA2_BONBU = dsGitaBonbu.GetColumn(0, "GITA2");

    var GITA1_CHANGWON = dsGitaChangwon.GetColumn(0, "GITA1");
    var GITA2_CHANGWON = dsGitaChangwon.GetColumn(0, "GITA2");

    var GITA1_BUSAN = dsGitaBusan.GetColumn(0, "GITA1");
    var GITA2_BUSAN = dsGitaBusan.GetColumn(0, "GITA2");

    var GITA1_SUM = GITA1_BONBU + GITA1_CHANGWON + GITA1_BUSAN;
    var GITA2_SUM = GITA2_BONBU + GITA2_CHANGWON + GITA2_BUSAN;

    dsGitaBusan.AddColumn("GITA1_SUM");
    dsGitaBusan.AddColumn("GITA2_SUM");

    Grid1.SetCellProp('Summ', 1, "text", GITA1_SUM);
	Grid1.SetCellProp('Summ', 2, "text", GITA2_SUM);
}


