function fcInit(obj) {
    dsPcTax.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    fcInit(obj);    
    return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "rsm5040-service:searchPcTax";
    var sInDataSet   = "";
    var sOutDataSet  = "dsPcTax=dsPcTax ";     
		sOutDataSet  += "dsCntChkValid=dsCntChkValid ";     
		sOutDataSet  += "dsCntSnd=dsCntSnd ";     


    //조회조건
    fcd_AddParam("PAY_YEAR", cboPayYear.Value);
    fcd_AddParam("MEET_CD" , cboMeetCdSearch.Value);
    fcd_AddParam("SELL_CD" , cboSellCdSearch.Value);
    // 광명에 해당되는 지점코드는 여러개이므로 정규식을 사용하여 조회한다.
    if(  cboDivNoSearch.Value == "00"){
		fcd_AddParam("BRNC_CD"      , "^(0[123468])$"); // 정규식표현		
    }else{
		fcd_AddParam("BRNC_CD"      ,  cboDivNoSearch.Value);		
    }

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true, "S");
}

