/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    // 조회 완료 후
    if ( sServiceName == "rsm5040-service:searchPcTax" ) {
		stCntChkValid.Text = "(일치 "+dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_T")+"건, 불일치 "+dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_F")+"건)";
       fcBtnControl();
    }

    // DB입력(검증)
    if ( sServiceName == "rsm5040-service:saveDbFile" ) {	

		fcSearch();

    }

    // 확정 완료 후
    if ( sServiceName == "rsm5010-service:saveCfmCntnt" ) {						 
		fcSearch();

    }

    // 1차확정 취소
    if( sServiceName == "rsm5010-service:saveCfmCancel") {
		fnc_Message(SNIS_CRA_B999, "N","확정취소가 완료되었습니다!");
		fcSearch();
    }
}

function fcBtnControl()
{
	dsPcTax.fireEvent = false;
	var nCnt = 0;
	dsPcTax.Filter("SND_CFM_CD=='001'||THR_CFM_CD=='001'");
	nCnt = dsPcTax.rowcount;
	dsPcTax.UnFilter();	
	dsPcTax.fireEvent = true;

	var nFstCfmCnt		= parseInt(dsCntSnd.GetColumn(0,"FST_CFM_CNT"));		// 1 차 확정 카운트
	var nSndCfmCnt		= parseInt(dsCntSnd.GetColumn(0,"SND_CFM_CNT"));	// 2 차 확정 데이터
	var nThrCfmCnt		= parseInt(dsCntSnd.GetColumn(0,"THR_CFM_CNT"));	// 3 차 확정 데이터
	var CNT_CHK_VALID_T = parseInt(dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_T"));	// 일치건
	var CNT_CHK_VALID_F = parseInt(dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_F"));	// 불일치건


	if(nFstCfmCnt > 0 && nFstCfmCnt==0) // 1차 확정까지만 되어 있을 때
	{
		CfmBtn.Enable = true;
		BtnCfmCancel.Enable = false;		
	}
	else
	{
		if( nSndCfmCnt > 0 && nThrCfmCnt == 0){ // 2차 확정이 되고 3차 확정 이 안되었을 때
			CfmBtn.Enable = false;
			BtnCfmCancel.Enable = true;		
		}else{
			if( nThrCfmCnt > 0){
				CfmBtn.Enable = false;
				BtnCfmCancel.Enable = false;	
			}else{
				if( CNT_CHK_VALID_T > 0 && CNT_CHK_VALID_F==0) {			
					CfmBtn.Enable = true;
					BtnCfmCancel.Enable = false;	
				}
				else
				{
					CfmBtn.Enable = false;
					BtnCfmCancel.Enable = false;	
				}
			}		
		}
	}
}
