/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

    //Return Key Press時 Next Component focus
    if ( nChar == ENTER_KEY ) {
        var oFocusComponent = GetNextComponent(true);
        oFocusComponent.setfocus();
        return;
    }
    //Control Key가 눌려져 있다면 단축키 처리
    //if (bControl) fnc_HotKey(this, nChar);
}


function fcBtnOnClick(obj) {



    switch(obj.id) {
        case "dbBtn":  //DB입력

			var sFilePath = dsFilePath.GetColumn(dsFilePath.currow, "CD_NM");	//폴더경로
			var sPayYear  = cboPayYear.Value;

			var sServiceName = "rsm5040-service:saveDbFile";
			var sInDataSet   = "";
			var sOutDataSet  = "";  	

			//서버로 보낼 값
			fcd_AddParam("FILE_PATH", sFilePath);
			fcd_AddParam("PAY_YEAR" , sPayYear);

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true, "U");
			break;

		case "cfmBtn":  //확정

			dsPcTax.fireEvent = false;
			var nCnt = 0;
			dsPcTax.Filter("SND_CFM_CD=='001'||THR_CFM_CD=='001'");
			nCnt = dsPcTax.rowcount;
			dsPcTax.UnFilter();	
			dsPcTax.fireEvent = true;

			var CNT_CHK_VALID_T = dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_T");
			var CNT_CHK_VALID_F = dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_F");
			// 기 확정 건이 존재
			if( nCnt > 0){
				fnc_Message(SNIS_CRA_B999, "N","기 확정건이 존재하므로 확정 하실 수 없습니다!");
				return;
			}

			//변경된 자료가 있는지 체크 => 변경된 자료가 있으면 작업 중지
            if ( fnc_DatasetChangeCheck(dsPcTax) ) {
                fnc_Message(SNIS_COM_1052, "N");
                return;
            }

            if( CNT_CHK_VALID_T == 0 ) {
                fnc_Message(SNIS_COM_1053, "N");
                return;
            }

            if(fnc_Message(SNIS_RBM_D003, "Y")) {           
                var sServiceName = "rsm5010-service:saveCfmCntnt";
                var sInDataSet   = "dsPcTax=dsPcTax:a ";
                var sOutDataSet  = "dsPcTax=dsPcTax ";    

                //서버로 넘길값
				fcd_AddParam("SEND_VALUE", "RSM5040");
				fcd_AddParam("PAY_YEAR", cboPayYear.Value);
				fcd_AddParam("MEET_CD" , cboMeetCdSearch.Value);
				fcd_AddParam("SELL_CD" , cboSellCdSearch.Value);
				// 광명에 해당되는 지점코드는 여러개이므로 정규식을 사용하여 조회한다.
				if(  cboDivNoSearch.Value == "00"){
					fcd_AddParam("BRNC_CD"      , "^(0[123468])$"); // 정규식표현		
				}else{
					fcd_AddParam("BRNC_CD"      ,  cboDivNoSearch.Value);		
				}


                fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
            }
			break;
		case "btnUpdateResNo":
            //변경된 자료가 있는지 체크
			if ( !fnc_DatasetChangeCheck(dsPcTax) ) {
				//저장 할 자료가 없음
				fnc_Message(SNIS_COM_1006, "N");    
				return false;
			}

            if(fnc_Message(SNIS_RBM_F022, "Y", "PC파일의 주민번호 변경")) {           
                var sServiceName = "rsm5040-service:UpdateCustRes";
                var sInDataSet   = "dsPcTax=dsPcTax:u ";
                var sOutDataSet  = "dsPcTax=dsPcTax ";    

                //서버로 넘길값
				fcd_AddParam("PAY_YEAR", cboPayYear.Value);
                fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
            }		
			break;
	}
}
// 광명항목이 표시되지 않는 경우때문에...
function dsBrncCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( nReason == 0){
		obj.fireEvent = false;
		var nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","01");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","02");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","03");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","04");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","06");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","08");
		obj.SetColumn(nRow,"CD_NM","광명");

		obj.fireEvent = true;
	}
}

// 확정취소
function btnCfmCancel_OnClick(obj)
{
	// 2차 이후 확정건이 존재하면 확정취소 불가판단
	dsPcTax.fireEvent = false;
	var nCnt = 0;
	dsPcTax.Filter("THR_CFM_CD=='001'");
	nCnt = dsPcTax.rowcount;
	dsPcTax.UnFilter();	

	dsPcTax.Filter("SEN_CFM_CD=='001'");
	var nCnt2 = dsPcTax.rowcount;
	dsPcTax.UnFilter();
	dsPcTax.fireEvent = true;

	var nSndCfmCnt		= parseInt(dsCntSnd.GetColumn(0,"SND_CFM_CNT"));	// 2 차 확정 데이터
	// 기 확정 건이 존재

	if( nCnt > 0){
		fnc_Message(SNIS_CRA_B999, "N","조회된 데이타 중 2차 이후 확정 건이 존재하므로 \n 확정취소 하실 수 없습니다!");		
		return;
	}

	// 확정 건이 존재는지 체크
	if( nSndCfmCnt == 0){
		fnc_Message(SNIS_CRA_B999, "N","조회된 데이타 중 2차 이후 확정 건이 존재하지 \n 않으므로 확정취소 하실 수 없습니다!");		
		return;
	}


	if( !fnc_Message(SNIS_CRA_B999, "Y","확정취소를 하시겠습니까?")) return; 

	var sServiceName = "rsm5010-service:saveCfmCancel";
	var sInDataSet   = "dsPcTax=dsPcTax ";
	var sOutDataSet  = "";    
	fcd_AddParam("SEND_VALUE"	, "SEN");	 // 2차 확정취소
	fcd_AddParam("PAY_YEAR", cboPayYear.Value);
	fcd_AddParam("MEET_CD" , cboMeetCdSearch.Value);
	fcd_AddParam("SELL_CD" , cboSellCdSearch.Value);
	// 광명에 해당되는 지점코드는 여러개이므로 정규식을 사용하여 조회한다.
	if(  cboDivNoSearch.Value == "00"){
		fcd_AddParam("BRNC_CD"      , "^(0[123468])$"); // 정규식표현		
	}else{
		fcd_AddParam("BRNC_CD"      ,  cboDivNoSearch.Value);		
	}
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
