+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    // 지불정지 화면은 저장버튼을 사용하지 않는다.(권한에서 제외)
    sBUTTONLIST   ='F'+sIsView +sInpt+'FTFT';     //초기화,조회,저장,삭제,엑셀,인쇄,닫기


 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	//fnc_SetBKColor(this);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 현재일자를 가져온다.
	fcCallSysDate();

	// 현재일기준으로 1개월전을 세팅한다.
	calToDate.Value = today();
	calFromDate.Value = addMonth(today(),-1);

	// 상태를 해지요청을 기본적으로 세팅한다.
	//cbStatus.Value = "003";
	Trace("JSESSIONID="+JSESSIONID);

	sDate = today();
}

