/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

    var strFromDate = calFromDate.Value;	// 발행시작일자
    var strToDate	= calToDate.Value;		// 발행종료일자    
	var strWinno  = edt_Winno.Text; 		// 창구번호
	var strTsnno  = edt_newTsn.Text; 		// tsn번호
	var strStatus = cbStatus.Value;			// 상태	

	if(Length(strWinno)>0) strWinno = "00"+strWinno;

	if( fnc_IsNull(strFromDate) ){			
		fnc_Message(SNIS_COM_1035, "N","발행기간일자");
		calFromDate.DropDown();
		return;
	}
	if( fnc_IsNull(strToDate) ){			
		fnc_Message(SNIS_COM_1035, "N","발행기간일자");
		calToDate.DropDown();
		return;
	}
	if( !fnc_IsDate(strFromDate) ){			
		fnc_Message(SNIS_CRA_F007, "N");
		calFromDate.DropDown();
		return;
	}
	if( !fnc_IsDate(strToDate) ){			
		fnc_Message(SNIS_CRA_F007, "N");
		calToDate.DropDown();
		return;
	}

	sServiceName = "rsm6020-service:searchTsn";				
	fcd_AddParam("fromdate"	, strFromDate);
	fcd_AddParam("todate"	, strToDate);
	fcd_AddParam("tnsno"	, strTsnno);
	fcd_AddParam("plcno"	, strWinno);


	if( strstatus == "000"){
		fcd_AddParam("ticstas"	, "^(00[1234])$");	// 전체일때 정규식으로 처리한다.
	}else{
		fcd_AddParam("ticstas"	, strStatus);	
	}	

    var sInDataSet   = "";
    var sOutDataSet  = "dsTsnResult=dsTsnResult";
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 저장가능유무처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  

	if( dsTsnResult.row < 0) return false;

	//변경된 자료가 있는지 체크	
	if ( !fnc_DatasetChangeCheck(dsTsnResult) ) {
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	/*	
	if( fnc_IsNull(txtTicCont.Text) ){			
		fnc_Message(SNIS_COM_1035, "N","티켓내용");
		txtTicCont.SetFocus();
		return;
	}
	*/
	for(var i=0;i<dsTsnResult.rowcount;i++) {
		if(dsTsnResult.GetRowType(i)=="insert") {
			fnc_Message( "[ " + (i+1) + " ]줄은 지불정지가 되지 않았기 때문에 저장하실 수 없습니다.", "N");
			return false;
		}
	}
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	if( !fcSaveCheck() ) return;

	dsTsnResult_S.ClearData();
	dsTsnResult_S.AddRow();
	dsTsnResult_S.CopyRow(0,"dsTsnResult",dsTsnResult.row);

	if( !getVeriData(dsTsnResult_S) ) return;	// 검증이 완료된건은 수정할수 없다.
	if( !fnc_Message(SNIS_CRA_B999, "Y","해당 데이타를 수정 하시겠습니까?")) return; 

	var sServiceName = "rsm6020-service:saveStoppayUpdate";
	var sInDataSet   = "dsTsnResult_S=dsTsnResult_S ";
	var sOutDataSet  = "";    

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


