/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	var strErr = ServiceMessage + "\n\n"+ ErrorMSG;

	if ( !fnc_result(ErrorCode, strErr) ) return;

    // 데이타조회
    if( sServiceName == "rsm6020-service:searchTsn"){
		// 최초 데이타의 해지요청 여부에 따른 데이타처리를 위하여..
		dsTsnResult.row = -1;
		dsTsnResult.row = 0;

		stTotCnt.Text = dsTsnResult.GetRowCount();
    }
    // 지불정지후 재조회
    if( sServiceName == "rsm6020-service:saveStopPay"){
		fnc_Message(SNIS_CRA_B999, "N","지불정지 등록이 완료되었습니다!");



		if(dsTsnResult_S.GetColumn(0,"MISS_REPO_CD") =="001" && dsTsnResult_S.GetColumn(0,"RCPT_STAT_CD") == "002" ){
			var strDesc = "";
			strDesc ="고객님의 분실경주권(구매권)이 확인되어 처리중입니다."+ chr(10);
			strDesc += "분실신고번호 : "+ dsTsnResult_S.GetColumn(0,"MISS_REPO_NO");
			fcSendSmsToCust(strDesc);
		}

		cbStatus.Value = "001";
		fcSearch();
	}
	// 지불정지 취소후 재조회
    if( sServiceName == "rsm6020-service:deleteStopPay"){
		fnc_Message(SNIS_CRA_B999, "N","지불정지 취소가 완료되었습니다!");
		cbStatus.Value = "003";
		fcSearch();
    }
    // 해지취소후 재조회
    if( sServiceName == "rsm6020-service:saveCnclCancel"){
		fnc_Message(SNIS_CRA_B999, "N","지불정지 해지취소가 완료되었습니다!");
		cbStatus.Value = "001";
		fcSearch();
    }
     // 수정저장후 재조회
    if( sServiceName == "rsm6020-service:saveStoppayUpdate"){
		fnc_Message(SNIS_CRA_B999, "N","데이타 변경이 완료되었습니다!");		
		fcSearch();
    }

    // 지불정지 해지 후 쪽지 및 SMS발송 후 재조회
	if( sServiceName == "rsm6020-service:saveCnclPay"){
		fnc_Message(SNIS_CRA_B999, "N","지불정지 해지가 완료되었습니다!");
		cbStatus.Value = "004";

		var strDesc = "";
		strDesc = "TSN : "+ dsTsnResult_S.GetColumn(0,"TSN_NO") +chr(10);
		strDesc += "분실신고번호 : "+ dsTsnResult_S.GetColumn(0,"MISS_REPO_NO");

		if     ( rdoSend.Value == "001") {fcSendmemo(strDesc, "");}	   	// 쪽지
		else if( rdoSend.Value == "002" ){fcSendSms(strDesc);	}  		// SMS

		// 지불정지일자와 해지일자가 다른 날인 경우 지점 관리자에게 무조건 쪽지 발송
		if (dsTsnResult_S.GetColumn(0,"STOPPAY_DT") <> dsTsnResult_S.GetColumn(0,"CNCL_DT")) {
			// 테스트용 문구 삽입
			strDesc += chr(10) + "forTest-->"+chr(10)+"STOPPAY_DT["+dsTsnResult_S.GetColumn(0,"STOPPAY_DT")+"], CNCL_DT["+dsTsnResult_S.GetColumn(0,"CNCL_DT")+"]";
			if (today() <> dsTsnResult_S.GetColumn(0,"STOPPAY_DT")) {
				fcSendmemo(strDesc, "M");
			}
		}
		fcSearch();
	}

	// 가져오기 후 재조회
    if( sServiceName == "rsm6020-service:saveStopPayInsert"){
		fnc_Message(SNIS_CRA_B999, "N","가져오기가 완료되었습니다!");		
		fcSearch();
    }

}

// 쪽지발송
function fcSendmemo(strDesc, strMngrYn)
{

	var sendUser	=	"";
	var recvUser	=	"";
	var title		=	"";
	var linkUrl		= 	"RSM6020";
	var desc		= 	"";
	var compId	 = "comSendMsg";

	sendUser = gdsUser.GetColumn(0,"USER_ID");
	if (strMngrYn == "M") {
		recvUser = dsTsnResult_S.GetColumn(0,"BRNC_MNGR_ID");		// 지점 담당자
	} else {	
		recvUser = dsTsnResult_S.GetColumn(0,"CNCL_REQ_ID");    	// 해지요청자
	}
	title	 = "지불정지 해제건";

	//발신사원번호, 수신사원번호, 제목, 링크정보, 내용	
	fcd_SendMessage(sendUser, recvUser, title, linkUrl, strDesc, compId);

}

//sms발송
function fcSendSms(strDesc)
{	
	if( ! fnc_IsNull( dsTsnResult_S.GetColumn(0,"CNCL_HP_NO") ) ){
		fcd_SendSms(	  gdsUser.GetColumn(0,"HP_NO")    			// 송신자 번호
						, gdsUser.GetColumn(0,"USER_ID")			// 송신자 사번 
						, gdsUser.GetColumn(0,"USER_NM")			// 송신자 성명
						, dsTsnResult_S.GetColumn(0,"CNCL_HP_NO")		// 수신자 번호
						, dsTsnResult_S.GetColumn(0,"CNCL_REQ_NM")	// 수신자명
						, strDesc 									// 문자내용(80Bytes이하)   
						, dsTsnResult_S.GetColumn(0,"CNCL_REQ_ID")	// 수신자아이디);
					);
		fnc_Message(SNIS_CRA_B999, "N","SMS발송이 완료되었습니다!");
	}
}


//sms발송
function fcSendSmsToCust(strDesc)
{

	if( ! fnc_IsNull( dsTsnResult_S.GetColumn(0,"CUST_PHONE_NO") ) ){
		fcd_SendSms(	  gdsUser.GetColumn(0,"HP_NO")    			// 송신자 번호
						, gdsUser.GetColumn(0,"USER_ID")			// 송신자 사번 
						, gdsUser.GetColumn(0,"USER_NM")			// 송신자 성명
						, dsTsnResult_S.GetColumn(0,"CUST_PHONE_NO")		// 수신자 번호
						, dsTsnResult_S.GetColumn(0,"CUST_NM")	// 수신자명
						, strDesc 									// 문자내용(80Bytes이하)   
						, dsTsnResult_S.GetColumn(0,"CUST_NM")	// 수신자아이디);
					);
		fnc_Message(SNIS_CRA_B999, "N","SMS발송이 완료되었습니다!");
	}
}

// TSN의 첫짜리 구하기
function btn_tsn_OnClick(obj)
{

	var strBeforTsn = edt_beforTsn.Text;
	if( fnc_IsNull(strBeforTsn) ){		
		fnc_Message(SNIS_CRA_B999, "N","TSN을 입력하십시요!");
		edt_beforTsn.SetFocus();
		return;
	}

	var checksum = 0;
	var digit 	 = 0;
	var tsn_org  = strBeforTsn;
	var tsn 	 = replace(replace(tsn_org,"-")," ","");	

	if ( fnc_IsNull(tsn) || length(tsn)!= 12) {		
		fnc_Message(SNIS_CRA_B999, "N","TSN이 정확한지 다시 확인하세요.");
		edt_beforTsn.SetFocus();
		return;
	}

	for(i=0; i<length(tsn);i++) {
		digit = 0;			
		cUnit = charAt(tsn,i);
		if( (cUnit >= "0") && (cUnit <= "9") ) {
			digit = asc(cUnit) - 48; 
		}else if ((cUnit >= 'A') && (cUnit <= 'F')) {
			digit = asc(cUnit) - 65 + 10; 
		}else if ((cUnit >= 'a') && (cUnit <= 'f')) { 
			digit = asc(cUnit) - 97 + 10; 
		}
		checksum = checksum + (digit * (i+1));
	}

	checksum = checksum%16;
	if (checksum >= 10) { 
		checksum = checksum + 65 - 10 ; 
	} else { 
		checksum = checksum + 48; 
	}	
	edt_afterTsn.Text = chr(checksum) + "-" + tsn_org;
	edt_newTsn.Text   = chr(checksum) + "-" + tsn_org;

	calFromDate.SetFocus();
}

// 분신신고구분 변경시
function cbMissRepoGbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if( strCode == "001"){
		imgSearch.Visible = true;

		dsTsnResult.SetColumn(dsTsnResult.row,"STOPPAY_REQ_DEPT",  sTeamNm);
		dsTsnResult.SetColumn(dsTsnResult.row,"STOPPAY_REQ_NM", sUserNm);

	}else{
		dsTsnResult.SetColumn(dsTsnResult.currow, "MISS_REPO_NO", "");

		dsTsnResult.SetColumn(dsTsnResult.currow,"CUST_NM",  "");
		dsTsnResult.SetColumn(dsTsnResult.currow,"CUST_PHONE_NO",  "");
		dsTsnResult.SetColumn(dsTsnResult.currow,"RCPT_STAT_CD",  "");


		dsTsnResult.SetColumn(dsTsnResult.row,"STOPPAY_REQ_DEPT",  "");
		dsTsnResult.SetColumn(dsTsnResult.row,"STOPPAY_REQ_NM", "");

		imgSearch.Visible = false;
	}
}

// 선택로우 변경시
function dsTsnResult_OnRowPosChanged(obj,nOldRow,nRow)
{	
	if( dsTsnResult.GetColumn(nRow, "MISS_REPO_CD") == "001"){
		imgSearch.Visible = true;
	}else{
		imgSearch.Visible = false;
	}

	if( dsTsnResult.GetRowType(nRow) == "insert"){
		calpubldt.Enable		= true; 	// 발행일자
		edtTsn.ReadOnly 		= false; 	// TSN
		edtTsn.BKColor 		    = "white"; 	// TSN
		cbMissRepoGbn.Enable 	= true; 	// 분실신고구분
		imgSearch.Enable		= true;		// 온라인접수목록조회				
		edtstoppayid.Enable		= true;
		edtstoppayReqnm.Enable	= true;

		// 지불정지버튼 활성화		
		btnStopPayCancel.Enable  = false; 	// 지불정지취소
		btnClcl.Enable			 = false; 	// 해지					
		btnStopPay.Enable		 = true;	// 지불정지	
		btnCnclCancel.Enable 	 = false;	// 해지취소		

		if(Length(edtTsn.Text) <=0){
			cbbrnc.Index = 0;
		}
	}else{
		calpubldt.Enable		= false;
		edtTsn.ReadOnly 		= true; 	// TSN
		edtTsn.BKColor 		    = "user61"; 	// TSN
		cbMissRepoGbn.Enable 	= false;
		imgSearch.Enable		= false;		
		edtstoppayid.Enable		= false;
		edtstoppayReqnm.Enable	= false;

		switch(dsTsnResult.getcolumn(nRow,"TIC_STAS"))
		{
		  case "001" : 	// 지불정지
				btnStopPayCancel.Enable  = true; 	// 지불정지취소
				btnClcl.Enable			 = true; 	// 해지					
				btnStopPay.Enable		 = false;	// 지불정지	
				btnCnclCancel.Enable 	 = false;	// 해지취소
				rdoSend.Value			 = "000";	// 사용안함

			  break;
		  case "002" :	// 해지요청
				btnStopPayCancel.Enable  = false; 	// 지불정지취소
				btnClcl.Enable			 = true; 	// 해지					
				btnStopPay.Enable		 = false;	// 지불정지	
				btnCnclCancel.Enable 	 = false;	// 해지취소
				//rdoSend.Value			 = "001";	// 쪽지
			  break;
		  case "003" :	// 해지요청
				btnStopPayCancel.Enable  = false; 	// 지불정지취소
				btnClcl.Enable			 = true; 	// 해지					
				btnStopPay.Enable		 = false;	// 지불정지	
				btnCnclCancel.Enable 	 = false;	// 해지취소
				//rdoSend.Value			 = "001";	// 쪽지
			  break;
		  case "004" :	// 해지
				btnStopPayCancel.Enable  = false; 	// 지불정지취소
				btnClcl.Enable			 = false; 	// 해지					
				btnStopPay.Enable		 = false;	// 지불정지	
				btnCnclCancel.Enable 	 = true;	// 해지취소
				rdoSend.Value			 = "000";
			  break;	  
		  default :			  
		}
	}	
}

// 접수고객 팝업실행
function imgSearch_OnClick(obj,nX,nY)
{
	var sFormId = "frm_rsm::RSM6020P.xml";
	var sArgs = "";	

	var rtn = Dialog(sFormId, sArgs, 530, 464, "");
	if( rtn.length > 0){

		edtMissRepoNo.Text = rtn[0];
		dsTsnResult.SetColumn(dsTsnResult.row,"CUST_NM",  rtn[1]);
		dsTsnResult.SetColumn(dsTsnResult.row,"CUST_PHONE_NO",  rtn[2]);
		dsTsnResult.SetColumn(dsTsnResult.row,"RCPT_STAT_CD",  rtn[3]);

	}
}

