// 지불정지 가져오기 처리
function btnStopPayInsert_OnClick(obj)
{
	if( !fnc_Message(SNIS_CRA_B999, "Y",substr(sDate,0,4) + "-" + substr(sDate,4,2) + "-" + substr(sDate,6,2) + "일 데이터를 가져오시겠습니까?")) return; 
	//if( !fnc_Message(SNIS_CRA_B999, "Y", substr(calFromDate.Value,0,4) + "-" + substr(calFromDate.Value,4,2) + "-" + substr(calFromDate.Value,6,2) +
	 //" ~ " + substr(calToDate.Value,0,4) + "-" + substr(calToDate.Value,4,2) + "-" + substr(calToDate.Value,6,2) + "일 데이터를 가져오시겠습니까?")) return; 

	var sServiceName = "rsm6020-service:saveStopPayInsert";
	var sInDataSet   = "";
	var sOutDataSet  = "";    

	fcd_AddParam("RACE_DAY", sDate);
	fcd_AddParam("RACE_YY", SUBSTR(sDate,0,4));
	fcd_AddParam("RACE_MM", SUBSTR(sDate,4,4));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

// 작업자 입력
function BtnUserInsert_OnClick(obj)
{
	if(trim(dsTsnResult.GetColumn(dsTsnResult.currow, "TIC_STAS")) == '001'){
		if(dsTsnResult.GetColumn(dsTsnResult.currow, "STOPPAY_NM") == "" || dsTsnResult.GetColumn(dsTsnResult.currow, "STOPPAY_NM") == null){
			if( fnc_Message(SNIS_CRA_B999, "Y","지불정지자 및 지불정지일을 등록하시겠습니까?")){
				dsTsnResult.SetColumn(dsTsnResult.currow, "STOPPAY_ID", sUserId);
				dsTsnResult.SetColumn(dsTsnResult.currow, "STOPPAY_NM", sUserNm);
				dsTsnResult.SetColumn(dsTsnResult.currow, "STOPPAY_DT", sDate);
			}
		}else if(dsTsnResult.GetColumn(dsTsnResult.currow, "STOPPAY_NM") != "" || dsTsnResult.GetColumn(dsTsnResult.currow, "STOPPAY_NM") != null){
			if( fnc_Message(SNIS_CRA_B999, "Y","지불정지자 및 지불정지일을 삭제하시겠습니까?")){
				dsTsnResult.SetColumn(dsTsnResult.currow, "STOPPAY_ID", "");
				dsTsnResult.SetColumn(dsTsnResult.currow, "STOPPAY_NM", "");
				dsTsnResult.SetColumn(dsTsnResult.currow, "STOPPAY_DT", "");
			}
		}
	}

	if(trim(dsTsnResult.GetColumn(dsTsnResult.currow, "TIC_STAS")) == '004'){
		if(dsTsnResult.GetColumn(dsTsnResult.currow, "CNCL_NM") == "" || dsTsnResult.GetColumn(dsTsnResult.currow, "CNCL_NM") == null){
			if( fnc_Message(SNIS_CRA_B999, "Y","해지자 및 해지일자를 등록하시겠습니까?")){
				dsTsnResult.SetColumn(dsTsnResult.currow, "CNCL_ID", sUserId);
				dsTsnResult.SetColumn(dsTsnResult.currow, "CNCL_NM", sUserNm);
				dsTsnResult.SetColumn(dsTsnResult.currow, "CNCL_DT", sDate);
			}
		}else if(dsTsnResult.GetColumn(dsTsnResult.currow, "CNCL_NM") != "" || dsTsnResult.GetColumn(dsTsnResult.currow, "CNCL_NM") != null){
			if( fnc_Message(SNIS_CRA_B999, "Y","해지자 및 해지일자를 삭제하시겠습니까?")){
				dsTsnResult.SetColumn(dsTsnResult.currow, "CNCL_ID", "");
				dsTsnResult.SetColumn(dsTsnResult.currow, "CNCL_NM", "");
				dsTsnResult.SetColumn(dsTsnResult.currow, "CNCL_DT", "");
			}
		}
	}
}

// 지불정지 처리
function btnStopPay_OnClick(obj)
{
	if( fnc_IsNull(calpubldt.Value) ){			
		fnc_Message(SNIS_COM_1035, "N","발행일자");
		calpubldt.SetFocus();
		return;
	}
	if( fnc_IsNull(rdoTicTyp.Value) ){			
		fnc_Message(SNIS_COM_1035, "N","티켓타입");
		rdoTicTyp.SetFocus();
		return;
	}
	if( fnc_IsNull(edtTsn.Text) ){			
		fnc_Message(SNIS_COM_1035, "N","TSN");
		edtTsn.SetFocus();
		return;
	}

	if( fnc_IsNull(cbBrnc.Value) ){			
		fnc_Message(SNIS_COM_1035, "N","지점");
		cbBrnc.SetFocus();
		return;
	}

	/*
	if( fnc_IsNull(edtstoppayid.Text) ){			
		fnc_Message(SNIS_COM_1035, "N","지불정지 요청부서");
		edtstoppayid.SetFocus();
		return;
	}
	if( fnc_IsNull(edtstoppayReqnm.Text) ){			
		fnc_Message(SNIS_COM_1035, "N","지불정지 요청자명");
		edtstoppayReqnm.SetFocus();
		return;
	}*/

	if( fnc_IsNull(txtTicCont.Text) ){			
		fnc_Message(SNIS_COM_1035, "N","티켓내용");
		txtTicCont.SetFocus();
		return;
	}
	if( cbMissRepoGbn.Value == "001" ) {// online
		if( fnc_IsNull(edtMissRepoNo.Text) ){			
			fnc_Message(SNIS_COM_1035, "N","온라인에 등록된 지불정지 등록시 분실신고번호");
			imgsearch.SetFocus();
			return;
		}	
	}
	if( !fnc_Message(SNIS_CRA_B999, "Y","지불정지 등록을 하시겠습니까?")) return; 

	dsTsnResult_S.ClearData();
	dsTsnResult_S.AddRow();
	dsTsnResult_S.CopyRow(0,"dsTsnResult",dsTsnResult.row);


	var sServiceName = "rsm6020-service:saveStopPay";
	var sInDataSet   = "dsTsnResult_S=dsTsnResult_S ";
	var sOutDataSet  = "";    

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

