}

// 지불정지 취소(데이타삭제)
function btnStopPayCancel_OnClick(obj)
{
	/*
		검증처리건은 취소불가
	*/	
	dsTsnResult_S.ClearData();
	dsTsnResult_S.AddRow();
	dsTsnResult_S.CopyRow(0,"dsTsnResult",dsTsnResult.row);

	//if( !getVeriData(dsTsnResult_S) ) return;	// 검증이 완료된건은 수정할수 없다.
	if( !fnc_Message(SNIS_CRA_B999, "Y","지불정지 취소를 하시겠습니까?\n(해당 데이타는 삭제됩니다)")) return; 

	var sServiceName = "rsm6020-service:deleteStopPay";
	var sInDataSet   = "dsTsnResult_S=dsTsnResult_S ";
	var sOutDataSet  = "";    

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

// 지불정지 해지 취소(지불정지상태로 되돌림)
function btnCnclCancel_OnClick(obj)
{

	if( dsTsnResult.row < 0) return;

	dsTsnResult_S.ClearData();
	dsTsnResult_S.AddRow();
	dsTsnResult_S.CopyRow(0,"dsTsnResult",dsTsnResult.row);

	//if( !getVeriData(dsTsnResult_S) ) return;	// 검증이 완료된건은 수정할수 없다.
	//if( !getDateCheck(dsTsnResult_S) ) return;	// 당일 지불정지건에 한에서만 해지취소가 가능하다.

	if( !fnc_Message(SNIS_CRA_B999, "Y","지불정지 해지 취소를 하시겠습니까?\n(해당 데이타는 지불정지상태로 변경됩니다)")) return; 




	var sServiceName = "rsm6020-service:saveCnclCancel";
	var sInDataSet   = "dsTsnResult_S=dsTsnResult_S ";
	var sOutDataSet  = "";    

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

// 변경가능한 데이타인지 체크
function getVeriData(objDs)
{		
	if( !fnc_isnull(objDs.GetColumn(0,"VERI_RSLT_DT")) ){
		fnc_Message(SNIS_CRA_B999, "N","DAS의 데이타검증이 완료된 건입니다");
		return false;
	}
	return true;	
}

// 해지-> 지불정지 변경 가능한지 체크
function getDateCheck(objDs)
{
	var strDate = dsDate.GetColumn(0,"YYYYMMDD");
	if( objDs.GetColumn(0,"CNCL_DT") != strDate){
		fnc_Message(SNIS_CRA_B999, "N","당일 처리건이 아니므로 지불정지로 변경이 불가능합니다!");
		return false;
	}
	return true;
}

