function fcReplaceSpace(str)
{
	//var strRe1 = "Tsn,Msn,Slot,Window,TELLER,Assoc,Comm,Div,Race,Race:,Meet,Perf,Trans,Type,:";
	var strRe1 = "Tsn,Msn,Slot,Window,TELLER,Assoc,Comm,Div,Race,Race:,Meet,Perf,Trans,Type,:Reader;,New,customer;,RECORD,LOCKED,NET,GROSS,WIN,AMT,GITA,PAY";
	var arrRe1 = split(strRe1,",");

	var rtn  = str;
	for(var i=0;i<length(arrRe1);i++){
		rtn = trim(replace(rtn,arrRe1[i],""));
	}
	return rtn;
}

