function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	if ( nReason == 0){
		obj.fireEvent = false;
		var nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","01");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","02");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","03");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","04");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","08");
		obj.SetColumn(nRow,"CD_NM","광명");

		obj.fireEvent = true;
	}
}

