/************************
*  공통 Script Include  *
************************/
#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";


/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	calRcptdate.Value = "";
	fcSearch();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearch(obj) {

	//필수 조회 조건 체크	
	var sServiceName = "rsm6020-service:searchRcpt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsRcpt=dsRcpt";

	fcd_AddParam("rcptdt", calRcptdate.Value ); 
	fcd_AddParam("custid", edtCustid.Text);
	fcd_AddParam("custnm", edtCustnm.Text);
	fcd_AddParam("TIC_STAS", chkYet.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj) {
	var rtnArray = array(6); 

    if( dsRcpt.row > -1){		
		rtnArray[0] =  dsRcpt.GetColumn(dsRcpt.row, "MISS_REPO_NO");
		rtnArray[1] =  dsRcpt.GetColumn(dsRcpt.row, "CUST_NM");
		rtnArray[2] =  dsRcpt.GetColumn(dsRcpt.row, "CUST_PHONE_NO");
		rtnArray[3] =  dsRcpt.GetColumn(dsRcpt.row, "RCPT_STAT_CD");

		this.Close(rtnArray);
    }
}

function fcClose(obj) {
	Close(false);
}

/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


}
/*-------------------------------------------------------------------------------------------------+
>>  조회조건에서 Enter key event 발생時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnterKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13) fcSearch();
}
/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}


function grdList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	fcChoice();
}
