/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	var strFromDate = calFromDate.Value;	// 발행시작일자
    var strToDate	= calToDate.Value;		// 발행종료일자    


	if( fnc_IsNull(strFromDate) ){			
		fnc_Message(SNIS_COM_1035, "N","접수일자");
		calFromDate.DropDown();
		return false;
	}
	if( fnc_IsNull(strToDate) ){			
		fnc_Message(SNIS_COM_1035, "N","접수일자");
		calToDate.DropDown();
		return false;
	}
	if( !fnc_IsDate(strFromDate) ){			
		fnc_Message(SNIS_RBM_A005, "N");
		calFromDate.DropDown();
		return false;
	}
	if( !fnc_IsDate(strToDate) ){			
		fnc_Message(SNIS_RBM_A005, "N");
		calToDate.DropDown();
		return false;
	}
	if( strFromDate > strToDate){	
		fnc_Message(SNIS_RBM_E002, "N");
		calToDate.DropDown();
		return false;
	}
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

	var strBrnc = cbBrnc.Value;
     strBrnc =  decode(strBrnc,"00","^(0[012348])$",strBrnc);


	var sServiceName = "rsm6070-service:searchReturns";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTsnRcptResult=dsTsnRcptResult ";
	var strTicStas = cbStatus.Value;

	if( strTicStas == "000"){
		fcd_AddParam("ticstas"	, "^(00[45])$");	// 전체일때 정규식으로 처리한다.
	}else{
		//fcd_AddParam("ticstas"	, strStatus);
		fcd_AddParam("ticstas"	, strTicStas);
	}	
	fcd_AddParam("tsnno"	, edtTsn.Text 		);
	fcd_AddParam("brnccd"	, strBrnc			);
	fcd_AddParam("custnm"	, edtCustNm.Text 	);
	fcd_AddParam("fromdate"	, calFromDate.Value );
	fcd_AddParam("todate"	, calToDate.Value 	);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

