/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if( sServiceName == "rsm6070-service:searchReturns"){
		// 환급취소 버튼의 활성화/비활성화를 위한 처리
		dsTsnRcptResult.row = -1;
		if( dsTsnRcptResult.rowcount > 0)	dsTsnRcptResult.row = 0;
   	}

   	if( sServiceName == "rsm6070-service:saveRefund"){
		// 환급처리 후 재조회
		fnc_Message(SNIS_CRA_B999, "N","환급처리가 완료되었습니다!");
		cbStatus.Value = "005";
		fcSearch();
   	}
   	if( sServiceName == "rsm6070-service:saveRefundCancel"){
		// 환급처리 취소 후 재 조회
		fnc_Message(SNIS_CRA_B999, "N","환급처리 취소가 완료되었습니다!");
		cbStatus.Value = "004";
		fcSearch();
   	}
}

