// 티켓상태를 필터링한다(환급관리 조건에 맞추어)
function dsTicStas_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if( nReason == 0){ 
		obj.fireEvent = false;
		obj.filter("CD_TERM3=='RSM6070'");
		obj.fireEvent = true;
	}	
}

// row변경시 환급,환급취소버튼 제어
function dsTsnRcptResult_OnRowPosChanged(obj,nOldRow,nRow)
{
	if( obj.getColumn(nRow,"TIC_STAS") == "005"){
		btnCancel.Enable = true;
		btnRefund.Enable = false;
	}else{
		btnCancel.Enable = false;
		btnRefund.Enable = true;
	}
}

// 환급취소
function btnCancel_OnClick(obj)
{
	if( !fnc_Message(SNIS_CRA_B999, "Y","환급처리를 취소 하시겠습니까?") ) return;

	var nRow = dsTsnRcptResult.row;
	dsTsnRcptResult_tmp.ClearData();
	dsTsnRcptResult_tmp.AddRow();
	dsTsnRcptResult_tmp.CopyRow(0,"dsTsnRcptResult",nRow);

	var sServiceName = "rsm6070-service:saveRefundCancel";
	var sInDataSet   = "dsTsnRcptResult=dsTsnRcptResult_tmp ";
	var sOutDataSet  = "";  	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

// 환급처리
function btnRefund_OnClick(obj)
{
	if( !fnc_Message(SNIS_CRA_B999, "Y","환급처리를 하시겠습니까?") ) return;

	if(Length(calRefundDt.Value) <=0){

		if(fnc_Message(SNIS_CRA_B999, "Y","오늘 날짜로 환급하시겠습니까?") ) {
			calRefundDt.Value = sCurDay;

		}else{

			calRefundDt.SetFocus();
			return;
		}

	}




	var nRow = dsTsnRcptResult.row;
	dsTsnRcptResult_tmp.ClearData();
	dsTsnRcptResult_tmp.AddRow();
	dsTsnRcptResult_tmp.CopyRow(0,"dsTsnRcptResult",nRow);
    dsTsnRcptResult_tmp.SetColumn(0,"REFUND_DEPT_NM", gdsUser.GetColumn(0,"DEPT_NM") );
    dsTsnRcptResult_tmp.SetColumn(0,"REFUND_ID", gdsUser.GetColumn(0,"USER_ID") );

	var sServiceName = "rsm6070-service:saveRefund";
	var sInDataSet   = "dsTsnRcptResult=dsTsnRcptResult_tmp ";
	var sOutDataSet  = "";  	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	 switch ( obj.id ) {
		case "dsBrncCdSearch" :
			dsBrncCdSearch.InsertRow(0);
			dsBrncCdSearch.SetColumn(0, "CD_NM", "전체");
			cbBrnc.index = 0;
			break;	

		case "dsBrncCd" :
			if ( nReason == 0){

				obj.fireEvent = false;
				var nRow  = obj.addrow();
				obj.SetColumn(nRow,"CD","01");
				obj.SetColumn(nRow,"CD_NM","광명");

				nRow  = obj.addrow();
				obj.SetColumn(nRow,"CD","02");
				obj.SetColumn(nRow,"CD_NM","광명");

				nRow  = obj.addrow();
				obj.SetColumn(nRow,"CD","03");
				obj.SetColumn(nRow,"CD_NM","광명");

				nRow  = obj.addrow();
				obj.SetColumn(nRow,"CD","04");
				obj.SetColumn(nRow,"CD_NM","광명");

				nRow  = obj.addrow();
				obj.SetColumn(nRow,"CD","08");
				obj.SetColumn(nRow,"CD_NM","광명");

				obj.fireEvent = true;

			}
			break;	

	}
}
