/***************************************************************************************************
*                                          화면 변수 선언부                                        *
***************************************************************************************************/
var sFormId       = this.GetForm().id;
var sBUTTONLIST   = "FFFFTFT";                         //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = "";                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = "";                                //Form의 Title 정보
var sFORMLOCATION = "";                                //Form의 Location 정보
var sUSERLAVEL    = "";                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트
var LEVEL         ;									   // 1:승인자(관리자)
                                                       // 2:요청자
                                                       // 3:조회
var SEND_GBN      ;	// C:반려  A:승인   R:요청
/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTFFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	//버튼이동
    aprvBackBtn.Top  = aprvReqBtn.Top;
    aprvBackBtn.Left = aprvReqBtn.Left;
    btnReconfirm.Top = aprvOkBtn.Top;
	btnReconfirm.left = aprvOkBtn.left;

	dsAprvStas.InsertRow(0);
	dsAprvStas.SetColumn(0, "CD_NM", "전체");
	if( sApro == "T" ) {
		//승인자
		LEVEL = 1;
		dsCommCd.InsertRow(0);
		dsCommCd.SetColumn(0, "CD_NM", "전체");
		cboCommNo.Enable = true;

		cboCommNo  .Index = 0;
		cboAprvStas.Index = dsAprvStas.FindRow("CD", "002");

		aprvOkBtn  .Visible = true;
		aprvBackBtn.Visible = true;
		btnImportUserTemp.Visible = true;
		grdPcTeller.SetColProp(12, "width", 105);
	} else {
		cboCommNo.Value   = gdsUser.GetColumn(0, "BRNC_CD");
		cboAprvStas.Index = 0;
		cboCommNo.Enable = false;

		if( sInpt == "T" ) {
			//요청자
			LEVEL = 2;
			aprvReqBtn.Visible = true;
		} else {
			//조회권한
			LEVEL = 3;
		}
		grdPcTeller.SetColProp(12, "width", 0);
	}

    fcSearch();   // 목록 조회 
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsPcTeller";
	if (!fnc_FormUnloadCheck(sDataset  , sFormId)) return false;
}

