function fcInit(obj) {
    dsPcTeller.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	edEmpNm.Text = trim(edEmpNm.Text);
   	fcInit(obj);	
	return true;
}

function fcSearch(obj) {
   	if ( !fcSearchCheck(obj)) return;

	var sServiceName = "rsm9010-service:searchPcTeller";
	var sInDataSet   = "";
	var sOutDataSet  = "dsPcTeller=dsPcTeller ";     

    var sAprvStas = "";

    fcd_AddParam("COMM_NO"  , cboCommNo.Value);	
	fcd_AddParam("APRV_STAS", cboAprvStas.Value);	
	fcd_AddParam("TELLER_NM", edEmpNm.Text);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}
/*-------------------------------------------------------------------------------------------------+
>>  버튼 클릭시
+-------------------------------------------------------------------------------------------------*/
function fcBtnCheck(sStas) {

	//버튼에 관계없는 공통처리
	if ( !fnc_DatasetChangeCheck(dsPcTeller) ) {
		//변경된 자료 없음
		fnc_Message(SNIS_COM_1053, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsPcTeller;
	sMandatoryColID   = "EMP_NO|TELLER_NM|COMM_NO|SELL_TYPE_CD|";
	sMandatoryColName = "사번|성명|지점|매표구분|";
	oGrid             = grdPcTeller;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	//버튼에 따른 처리
	if(sStas == "req") {	//승인요청

		for(var i=0;i<dsPcTeller.rowcount;i++) {
			if( dsPcTeller.GetRowType(i) != "normal" ) {
				if( fnc_IsNull(dsPcTeller.GetColumn(i, "ETC")) ) {
					grdPcTeller.SetCellPos(grdPcTeller.GetBindCellIndex("body", "ETC"));
					fnc_Message(SNIS_CRA_0001 ,"N", i+1, "비고");
					return false;
				}

				if(dsPcTeller.GetColumn(i, "PROC_GBN")=="001") {
					grdPcTeller.SetCellPos(grdPcTeller.GetBindCellIndex("body", "PROC_GBN"));
					fnc_Message(SNIS_RBM_D012 ,"N", i+1, dsProcGbn.LookUp("CD", "001", "CD_NM"));
					return false;
				}
			}
		}
	} else if( sStas == "aprv") {	//승인
		var nCnt = 0;

		for(var i=0; i < dsPcTeller.rowcount; i++) {
			if( dsPcTeller.GetColumn(i, "CHK") == "1" ) {
				if( !IsDigit( dsPcTeller.GetColumn(i, "TELLER_ID") ) ||
					length(dsPcTeller.GetColumn(i, "TELLER_ID")) != 4 ) {
					fnc_Message(SNIS_RBM_D014, "N");
					return false;
				}
				if( !IsDigit( dsPcTeller.GetColumn(i, "PASSWD") ) ||
					length(dsPcTeller.GetColumn(i, "PASSWD")) < 1 ) {
					fnc_Message(SNIS_RBM_D020, "N");
					return false;
				}

				if( dsPcTeller.GetColumn(i, "APRV_STAS") != "002" ) {
					fnc_Message(SNIS_RBM_D016, "N", "승인");
					return false;
				}
				nCnt++;
			}
		}

		if( dsPcTeller.GetDelRowCount() == 0 && nCnt == 0 ) {
			fnc_Message(SNIS_COM_1053, "N");
			return false;
		}
	} else if( sStas == "cancel") {	//반려
		var nCnt = 0;

		if( dsPcTeller.GetDelRowCount() > 0 ) {
			fnc_Message(SNIS_RBM_D013, "N");
			return false;
		}

		for(var i=0; i < dsPcTeller.rowcount; i++) {
			if( dsPcTeller.GetColumn(i, "CHK") == "1" ) {
				if( dsPcTeller.GetColumn(i, "COMM_NO") != 
					dsPcTeller.GetColumn(i, "ORG_COMM_NO") ||
					dsPcTeller.GetColumn(i, "TELLER_ID") != 
					dsPcTeller.GetColumn(i, "ORG_TELLER_ID") ||
					dsPcTeller.GetColumn(i, "EMP_NO") != 
					dsPcTeller.GetColumn(i, "ORG_EMP_NO") ||
					dsPcTeller.GetColumn(i, "SELL_TYPE_CD") != 
					dsPcTeller.GetColumn(i, "ORG_SELL_TYPE_CD") ) {

					fnc_Message(SNIS_RBM_D015, "N", i+1);
					return false;
				}

				if( dsPcTeller.GetColumn(i, "APRV_STAS") != "002" ) {
					fnc_Message(SNIS_RBM_D016, "N", "반려");
					return false;
				}
				nCnt++;
			}
		}

		if( nCnt == 0 ) {
			fnc_Message(SNIS_COM_1053, "N", i+1);
			return false;
		}
	} else if( sStas == "ReConfirm") {	// 암호 확인
		var nCnt = 0;

		if( dsPcTeller.GetDelRowCount() > 0 ) {
			fnc_Message(SNIS_RBM_D013, "N");
			return false;
		}

		for(var i=0; i < dsPcTeller.rowcount; i++) {
			if( dsPcTeller.GetColumn(i, "CHK") == "1" ) {
				if( dsPcTeller.GetColumn(i, "APRV_STAS") != "003" ) {
					fnc_Message(SNIS_RBM_D016, "N", "암호 확인");
					return false;
				}
				nCnt++;
			}
		}

		if( nCnt == 0 ) {
			fnc_Message(SNIS_COM_1053, "N", i+1);
			return false;
		}
	}

	return true;
}

//버튼 클릭
function fcBtnOnClick(obj) {	
	switch(obj.id) {
		case "aprvReqBtn" :	//승인요청
			if ( !fcBtnCheck("req")) return;

			if(fnc_Message(SNIS_RBM_F032, "Y", "승인요청")) {           
				var sServiceName = "rsm9010-service:saveTeller";
				var sInDataSet   = "dsPcTeller=dsPcTeller:u ";
				var sOutDataSet  = "dsPcTeller=dsPcTeller ";  	

				fcd_AddParam("LEVEL", LEVEL);
				fcd_AddParam("APRV_STAT", "REQ");

				SEND_GBN = "R";

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
		break;

		case "aprvOkBtn" :	//승인
			if ( !fcBtnCheck("aprv")) return;

			if(fnc_Message(SNIS_RBM_F032, "Y", "승인")) {           
				var sServiceName = "rsm9010-service:saveTeller";
				var sInDataSet   = "dsPcTeller=dsPcTeller:u ";
				var sOutDataSet  = "dsPcTeller=dsPcTeller ";  	
				fcd_AddParam("LEVEL"    , LEVEL);
				fcd_AddParam("APRV_STAT", "ARPV");

				SEND_GBN = "A";
				fcEmpSet(); //쪽지 대상자 Set
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
		break;

		case "aprvBackBtn" :	//반려
			if ( !fcBtnCheck("cancel")) return;

			if(fnc_Message(SNIS_RBM_F032, "Y", "반려")) {           
				var sServiceName = "rsm9010-service:saveTeller";
				var sInDataSet   = "dsPcTeller=dsPcTeller:u ";
				var sOutDataSet  = "dsPcTeller=dsPcTeller ";  	

				fcd_AddParam("LEVEL"    , LEVEL);
				fcd_AddParam("APRV_STAT", "CANCEL");

				SEND_GBN = "C";
				fcEmpSet(); //쪽지 대상자 Set
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
		break;

		case "btnReconfirm" :	// 암호 확인
			if ( !fcBtnCheck("ReConfirm")) return;

			if(fnc_Message(SNIS_RBM_F032, "Y", "암호확인")) {           
				var sServiceName = "rsm9010-service:saveTeller";
				var sInDataSet   = "dsPcTeller=dsPcTeller:u ";
				var sOutDataSet  = "dsPcTeller=dsPcTeller ";  	

				fcd_AddParam("LEVEL"    , LEVEL);
				fcd_AddParam("APRV_STAT", "RECORM");

				SEND_GBN = "";
				fcEmpSet(); //쪽지 대상자 Set
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
		break;

		case "btnImportUserTemp":
			if(fnc_Message("직원정보를 다시 가져오시겠습니까?", "Y")) {           
				var sServiceName = "rsm9010-service:ImportUserTemp";
				var sInDataSet   = "";
				var sOutDataSet  = "";  	
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}		
		break;
	}
}

