/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//if( LEVEL == "2" ) {
		var nRow = dsPcTeller.AddRow();
		dsPcTeller.SetColumn(nRow, "PROC_GBN"     , "002");	
		dsPcTeller.SetColumn(nRow, "APRV_STAS"    , "001");	
		dsPcTeller.SetColumn(nRow, "ORG_APRV_STAS", "001");	
	//} else {
	//	fnc_Message(SNIS_RBM_D011, "N");
	//}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if( LEVEL == "1" ) {
		// Check한 항목중, 처리여부가 삭제가 아닌 값이 있다면 삭제 불가능
		for( var i=0; i < dsPcTeller.rowcount; i++) {
			if( dsPcTeller.GetColumn(i, "CHK") == "1" ) {
				if( dsPcTeller.GetColumn(i, "PROC_GBN") != "004" ) {
					fnc_Message(SNIS_CRA_B999, "N", "처리여부가 [ 삭제 ]인 항목에 대해서만 삭제가 가능합니다.");
					return;
				}
			}
		}

		if ( !fnc_DeleteCheck(dsPcTeller, "CHK", "1") ) return;
    } else if( LEVEL == "2" ) {
		if ( !fnc_DeleteCheck(dsPcTeller, "CHK", "1") ) return;
	} else if( LEVEL == "3" ) {
		fnc_Message(SNIS_RBM_D011, "N");
	}
}

/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rsm9010-service:searchPcTeller" ) {
		if( sApro != "T" ) {
			if (dsPcTeller.SearchRow("APRV_STAS='003'") >= 0) {
				btnReconfirm.Visible = true;
			} else {
				btnReconfirm.Visible = false;
			}
		}		

	}			

	// 저장 완료 후
	if ( sServiceName == "rsm9010-service:saveTeller" ) {
		var ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE,"^","=");
        var sResult = fnc_Array2Value(ARRAY2_GBL_RET_VALUE, "RESULT"); // 결과

		if( sResult == "S" ) {		// 성공시에만 쪽지 발송
			if( SEND_GBN == "R" ) {	// 발매원관리 요청
				fcSendMngMsgReq(); 
			} else if(SEND_GBN == "A" || SEND_GBN == "C") {	// 발매원관리 승인
				fcSendEmp(SEND_GBN);          
			}
		}
		fcSearch();
	}
}

//컬럼 변경시
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {

	if( strColumnID == "PROC_GBN" ) {
		if( obj.GetRowType(nRow) != "insert" &&
			varNewValue == "002"             ) {
			obj.SetColumn(nRow, strColumnID, "001");
			fnc_Message(SNIS_RBM_D010, "N");
		}
	}
}


//그리드 팝업버튼
function fcGrdOnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {

	var sFormId 	= "frm_rsm::RSM9011.xml";
	var sArgs 		= "sCommNo="+quote(cboCommNo.Value);
	var arrReturn   = array(6);

	if (nCell == 3 ) {
		arrReturn = Dialog(sFormId, sArgs, 334, 443, "");

		if ( length(arrReturn) > 0  ) {

			dsPcTeller.SetColumn(nRow, "COMM_NO"     , arrReturn[0]);
			dsPcTeller.SetColumn(nRow, "COMM_NM"     , arrReturn[1]);
			dsPcTeller.SetColumn(nRow, "EMP_NO"      , arrReturn[2]);
			dsPcTeller.SetColumn(nRow, "TELLER_NM"   , arrReturn[3]);
			dsPcTeller.SetColumn(nRow, "SELL_TYPE_CD", arrReturn[4]);
			dsPcTeller.SetColumn(nRow, "TELLER_GBN"  , arrReturn[5]);
		}
	}
}

//쪽지 전송_요청자가 승인자에게
function fcSendMngMsgReq(sTitle, sDesc){
	var sSendUserId   = gdsUser.GetColumn(0,"USER_ID");
	var sSendUserNm   = gdsUser.GetColumn(0,"USER_NM");
	var sSendUserHpNo = gdsUser.GetColumn(0,"HP_NO");
	var sTitle        = "발매원 승인요청건";
	var sDesc         = "[ 발매원 관리 ]의 승인요청이 들어왔습니다.";

	fcd_SendMsgByAlarmDs("006", sSendUserId, sSendUserNm, sSendUserHpNo, sTitle, sDesc, "RSM9010");			
}

//쪽지 전송_승인자가 요청자에게
function fcSendMngMsgAprv(sRecvUser, sTitle, sDesc){
	var sSendUser = gdsUser.GetColumn(0,"USER_ID");

	// 송신자 사번, 수신자 사번, 제목, 화면ID, 내용, 구분값
	fcd_SendMessage(sSendUser, sRecvUser, sTitle, "RSM9010",  sDesc, "comSendMsg");									
}

//쪽지 대상자 Setting 
function fcEmpSet() {
	var sEmpNo = "";
	var nAddRow;
	var nFindRow;

	dsSendEmp.ClearData();
	for(var i=0; i < dsPcTeller.rowcount; i++) {
		if( dsPcTeller.GetColumn(i, "CHK") == "1" ) {
			//sEmpNo   = dsPcTeller.GetColumn(i, "EMP_NO");	
			sEmpNo   = dsPcTeller.GetColumn(i, "REQ_EMP_NO");		// 사용자가 아닌 요청자로 변경(2012.04.26)
			nFindRow = dsSendEmp.FindRow("EMP_NO", sEmpNo);

			if( nFindRow == -1 ) {
				nAddRow = dsSendEmp.AddRow();
				dsSendEmp.SetColumn(nAddRow, "EMP_NO", sEmpNo);
				dsSendEmp.SetColumn(nAddRow, "CNT"   , 1);
			} else {
				dsSendEmp.SetColumn(nFindRow, "CNT", dsSendEmp.GetColumn(nFindRow, "CNT") + 1);
			}
		}
	}

	for(var i=0; i< dsPcTeller.GetDelRowCount(); i++) {
		//sEmpNo   = dsPcTeller.GetDelColumn(i, "EMP_NO");
		sEmpNo   = dsPcTeller.GetColumn(i, "REQ_EMP_NO");		// 사용자가 아닌 요청자로 변경(2012.04.26)
		nFindRow = dsSendEmp.FindRow("EMP_NO", sEmpNo);

		if( nFindRow == -1 ) {
			nAddRow = dsSendEmp.AddRow();
			dsSendEmp.SetColumn(nAddRow, "EMP_NO", sEmpNo);
			dsSendEmp.SetColumn(nAddRow, "CNT"   , 1);
		} else {
			dsSendEmp.SetColumn(nFindRow, "CNT", dsSendEmp.GetColumn(nFindRow, "CNT") + 1);
		}
	}
}

//승인,반려 쪽지 보내기( sGbn   A;승인   C:반려  )
function fcSendEmp(sGbn) {
	var sComment = "";

	if( sGbn == "A" ) {
		sComment = "승인";
	} else {
		sComment = "반려";
	}

	for(var i=0; i < dsSendEmp.rowcount; i++) {
		var sRecvUser = dsSendEmp.GetColumn(i, "EMP_NO");
		var sTitle    = "발매원관리"+ sComment + "처리";
		var sDesc     =  "요청하신 발매원 관리가" 
					  + sComment + "되었습니다."
					  + "확인해주세요";

		fcSendMngMsgAprv(sRecvUser, sTitle, sDesc);               
	}
}
