Window.Script:
#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";

/*********************
*  화면 변수 선언부  *
*********************/
var sCommNo     = "";
var sCommNm     = "";
var sEmpNO      = "";
var sEmpNm      = "";
var sSellTypeCd = "";
var sTellerGbn  = ""; 

/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){
	//공통코드 조회
	fcd_GetComboDs(dsInComCd);
	if (IsExistVar("sCommNo")) {
		cboCommNoP.value = sCommNo;
	}
	fcSearch();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearchCheck(obj) {
    dsTeller.ClearData();
    dsEmp   .ClearData();
    fcGita();
    return true;
}

function fcSearch(obj)
{	
	if ( !fcSearchCheck(obj) ) return;

	var	sServiceName = "rsm9010-service:searchTeller";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTeller=dsTeller "
                     + "dsEmp=dsEmp ";

	fcd_AddParam("COMM_NO", cboCommNoP.Value);
	fcd_AddParam("EMP_NM" , edEmpNm.Text);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rsm9011-service:searchTeller" ) {
		edEmpNm.SetFocus();
	}
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj) {

	var nRow = -1;

	switch( tab.TabIndex ) {
		case 0 :
			nRow = dsTeller.FindRow("CHK", "1");

			sCommNo     = dsTeller.GetColumn(nRow, "COMM_NO");
			sCommNm     = dsTeller.GetColumn(nRow, "COMM_NM");
			sEmpNO      = dsTeller.GetColumn(nRow, "EMP_NO");
			sEmpNm      = dsTeller.GetColumn(nRow, "EMP_NM");
			sSellTypeCd = dsTeller.GetColumn(nRow, "SELL_TYPE_CD");
			sTellerGbn  = dsTeller.GetColumn(nRow, "TELLER_GBN");
		break;
		case 1 :
			nRow = dsEmp.FindRow("CHK", "1");

			sCommNo     = dsEmp.GetColumn(nRow, "COMM_NO");
			sCommNm     = dsEmp.GetColumn(nRow, "COMM_NM");
			sEmpNO      = dsEmp.GetColumn(nRow, "EMP_NO");
			sEmpNm      = dsEmp.GetColumn(nRow, "EMP_NM");
			sSellTypeCd = dsEmp.GetColumn(nRow, "SELL_TYPE_CD");
			sTellerGbn  = dsEmp.GetColumn(nRow, "TELLER_GBN");
		break;
		case 2 :
			nRow = 0;

			sCommNo     = dsGita.GetColumn(nRow, "COMM_NO");
			sCommNm     = dsCommNo.LookUP("CD", sCommNo, "CD_NM");
			sEmpNO      = dsGita.GetColumn(nRow, "EMP_NO");
			sEmpNm      = dsGita.GetColumn(nRow, "EMP_NM");
			sSellTypeCd = dsGita.GetColumn(nRow, "SELL_TYPE_CD");
			sTellerGbn  = dsGita.GetColumn(nRow, "TELLER_GBN");
		break;

	}

	var arrReturn = array(6);  //결과 반환 할 Array	

	arrReturn[0] = sCommNo;  
	arrReturn[1] = sCommNm;     
	arrReturn[2] = sEmpNO; 
	arrReturn[3] = sEmpNm; 
	arrReturn[4] = sSellTypeCd; 
	arrReturn[5] = sTellerGbn; 

	if( nRow != -1 ) {
		if( !fcValild(arrReturn) ) return;
	}

    Close(arrReturn);                                                 
}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) {
	Close(false);     //Null 반환 후 종료
}

/*---------------------------------+
|  Enter Key Press 시 조회 실행 !  |
+----------------------------------*/
function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if ( nChar == ENTER_KEY ) fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

/*-------------------------------------------------------------------------------------------------+
>>  컬럼 변경 시
+-------------------------------------------------------------------------------------------------*/
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch (obj.id ) {
		case "dsTeller" :
		case "dsEmp" :
			if( strColumnID == "CHK" && varNewValue == 1 ) {
				for(var i=0;i<obj.rowcount;i++) {
					if(i != nRow)	obj.SetColumn(i,"CHK",0); 
				}
			}
		break;		
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  더블클릭
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	var arrReturn    = array(5);  

	switch (obj.id ) 
	{
		case "grdTeller" :
			arrReturn[0] = dsTeller.GetColumn(nRow, "COMM_NO");
			arrReturn[1] = dsTeller.GetColumn(nRow, "COMM_NM");
			arrReturn[2] = dsTeller.GetColumn(nRow, "EMP_NO");
			arrReturn[3] = dsTeller.GetColumn(nRow, "EMP_NM");
			arrReturn[4] = dsTeller.GetColumn(nRow, "SELL_TYPE_CD");
			arrReturn[5] = dsTeller.GetColumn(nRow, "TELLER_GBN");

			if( !fcValild(arrReturn) ) return;
			Close(arrReturn);
		case "grdEmp"    :
			arrReturn[0] = dsEmp.GetColumn(nRow, "COMM_NO");
			arrReturn[1] = dsEmp.GetColumn(nRow, "COMM_NM");
			arrReturn[2] = dsEmp.GetColumn(nRow, "EMP_NO");
			arrReturn[3] = dsEmp.GetColumn(nRow, "EMP_NM");
			arrReturn[4] = dsEmp.GetColumn(nRow, "SELL_TYPE_CD");	
			arrReturn[5] = dsEmp.GetColumn(nRow, "TELLER_GBN");

			if( !fcValild(arrReturn) ) return;
			Close(arrReturn);
		case "grdGita"   :
			arrReturn[0] = dsGita.GetColumn(nRow, "COMM_NO");
			arrReturn[1] = dsCommNo.LookUP("CD", arrReturn[0], "CD_NM");
			arrReturn[2] = dsGita.GetColumn(nRow, "EMP_NO");
			arrReturn[3] = dsGita.GetColumn(nRow, "EMP_NM");
			arrReturn[4] = dsGita.GetColumn(nRow, "SELL_TYPE_CD");	
			arrReturn[5] = dsGita.GetColumn(nRow, "TELLER_GBN");

			if( !fcValild(arrReturn) ) return;
			Close(arrReturn);
		break;
	}	
}

/*-------------------------------------------------------------------------------------------------+
>>  기타입력
+-------------------------------------------------------------------------------------------------*/
function fcGita() {
	dsGita.ClearData();

	var nRow = dsGita.AddRow();

	dsGita.SetColumn(nRow, "CHK"       , "0");
	dsGita.SetColumn(nRow, "TELLER_GBN", "003");
}

function fcValild(arrReturn) {
	if( fnc_IsNull(arrReturn[0]) ) {
		fnc_Message(SNIS_COM_1035, "N", "지점");
		return false;
	}

	if( fnc_IsNull(arrReturn[2]) ) {
		fnc_Message(SNIS_COM_1035, "N", "사번");
		return false;
	}

	if( fnc_IsNull(arrReturn[3]) ) {
		fnc_Message(SNIS_COM_1035, "N", "성명");
		return false;
	}

	if( fnc_IsNull(arrReturn[4]) ) {
		fnc_Message(SNIS_COM_1035, "N", "매표구분");
		return false;
	}
	return true;
}
