function fcInit(obj) {
	dsUser.ClearData();
	dsTempUser.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;


	fcSearchUser();

	fcSearchTempUser();

	fcSearchEstmUser();
}


function fcSearchUser(){

	var sServiceName = "rsy3050-service:searchUser";
	var sInDataSet   = "";
	var sOutDataSet  = "dsUser=dsUser";

	fcd_AddParam("USER_NM", edSearchUserNm.Text);
	fcd_AddParam("GBN", sGnrUserGbn);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

function fcSearchTempUser(){
	var sServiceName = "rsy3050-service:searchTempUser";
	var sInDataSet   = "";
	var sOutDataSet  = "dsTempUser=dsTempUser";

	fcd_AddParam("USER_NM", edSearchUserNm.Text);
	fcd_AddParam("GBN", sTempUserGbn);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}



function fcSearchEstmUser(){
	var sServiceName = "rsy3050-service:searchEstmUser";
	var sInDataSet   = "";
	var sOutDataSet  = "dsEstmUser=dsEstmUser";

	fcd_AddParam("USER_NM", edSearchUserNm.Text);
	fcd_AddParam("GBN", sEstmUserGbn);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsTempUser) && !fnc_DatasetChangeCheck(dsEstmUser)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsTempUser;
	sMandatoryColID   = "USER_NM|USER_ID|PSWD";
	sMandatoryColName = "사용자명|사용자ID|비밀번호";
	oGrid             = tbUserMana.tbTempUser.grdTempUser;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	// 필수항목체크
	oDataSet          = dsEstmUser;
	sMandatoryColID   = "USER_NM|USER_ID|PSWD";
	sMandatoryColName = "사용자명|사용자ID|비밀번호";
	oGrid             = tbUserMana.tbEstmUser.grdEstmUser;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {    
	if ( !fcSaveCheck() ) return;


	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rsy3050-service:saveUser";
	sInDataSet   = "dsTempUser=dsTempUser:u dsEstmUser=dsEstmUser:u";

	sOutDataSet  = "dsTempUser=dsTempUser ";

	fcd_AddParam("JOB_GBN", "01");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :

		  if(tbUserMana.TabIndex == 1){
			var nRow = dsTempUser.AddRow(); 

			dsTempUser.SetColumn(nRow,"ASSO_CD","GA120000" );
			dsTempUser.SetColumn(nRow,"ASSO_NM","경륜·경정사업본부");
			dsTempUser.SetColumn(nRow,"GBN",sTempUserGbn );

			tbUserMana.tbTempUser.grdTempUser.SetCellPos(1);
			tbUserMana.tbTempUser.grdTempUser.SetFocus(); // 입력 첫 항목으로 Focus

		  }else if(tbUserMana.TabIndex == 2){
		  	var nRow = dsEstmUser.AddRow(); 

			dsEstmUser.SetColumn(nRow,"ASSO_CD","GA120000" );
			dsEstmUser.SetColumn(nRow,"ASSO_NM","경륜·경정사업본부");
			dsEstmUser.SetColumn(nRow,"GBN",sEstmUserGbn );

			tbUserMana.tbEstmUser.grdEstmUser.SetCellPos(1);
			tbUserMana.tbEstmUser.grdEstmUser.SetFocus(); // 입력 첫 항목으로 Focus
		  }


		break;

	}
}


