>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
		   if(tbUserMana.TabIndex == 1){
				if ( !fnc_DeleteCheck(dsTempUser, "chk", "1") ) return;

		  }else if(tbUserMana.TabIndex == 2){
				if ( !fnc_DeleteCheck(dsEstmUser, "chk", "1") ) return;

		  }

			break;

	}
}


