function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	var sMsg = ServiceMessage;
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 사용자  조회 완료 후
	if ( sServiceName == "rsy3050-service:searchUser" ) {


	}


	// 사용자 저장 완료 후
	if ( sServiceName == "rsy3050-service:saveUser" ) {

		fcSearch();
	}


}




