//암호변경 버튼 클릭 시 권한이양 팝업을 호출한다.
function fcBtnOnClick(obj)
{
	 switch ( obj.id ) {
		case "btnSavePw" :	

			  var dsObj;
			  if(tbUserMana.TabIndex == 1){
					dsObj = dsTempUser;

			  }else if(tbUserMana.TabIndex == 2){
					dsObj = dsEstmUser;
			  }


			//선택된 Row Count
			var iSelectRowCount = fnc_GetColumnValueCount(dsObj , "CHK", "1");

			if ( iSelectRowCount <= 0 ) {
				fnc_Message(SNIS_CRA_Z003, "N");
				return false;
			}

			var nRow = dsObj .FindRowNF("CHK","1");
			var sGbn = dsObj .GetColumn(nRow,"GBN");
			var sUserId = dsObj .GetColumn(nRow,"USER_ID");

			var rtn = Open("frm_rsy::RSY3051.xml", "sGbn="+quote(sGbn)+" sUserId="+quote(sUserId), 130, 250, "Title=false");


			break;

		case "btnImportUser":

			var sServiceName = "rsy3050-service:importUser";
			var sInDataSet   = "";
			var sOutDataSet  = "";

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

			break;

	}

}


function fctabOnChanged(obj,nOldIndex,nNewindex)
{
	if (obj.id == "tbUserMana") {
		if (nNewIndex == 0) btnImportUser.Visible = true;
		else                btnImportUser.Visible = false;
	}
}
