/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	switch ( obj.ID ) {
		case "btnCracel": //초기화 
				dsInfraEquip.ClearData();

				dsInfraMapp.ClearData();
				tbInfra.tbEquipHist.divEquipHist.fcInit();

				tbInfra.TabIndex = 0;

				break;
		case "btnSearch": //조회 
				dsInfraEquip.ClearData();

				dsInfraMapp.ClearData();
				tbInfra.tbEquipHist.divEquipHist.fcInit();
				break;

		case "dsInfraEquip": // 그리드 
				dsInfraMapp.ClearData();
				tbInfra.tbEquipHist.divEquipHist.fcInit();
				break;
		default: 
				dsInfraEquip.ClearData();

				dsInfraMapp.ClearData();
				tbInfra.tbEquipHist.divEquipHist.fcInit();
				break;
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj,sDistNo,sEquipGbn) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	switch ( obj.Id ) {
		case "btnSearch":

			 sServiceName = "rsy4020-service:searchInfraEquip";
			 sInDataSet   = "";
			 sOutDataSet  = "dsInfraEquip=dsInfraEquip ";

			 //조회조건 : 
			 fcd_AddParam("TPE_1", cboSearchTpe1.Value);
			 fcd_AddParam("TPE_2", cboSearchTpe2.Value);
			 fcd_AddParam("EQUIP_NM", edSearchEquipNm.Text);

			 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);


			break;

		case "dsInfraEquip" :			 

			 tbInfra.tbEquipHist.divEquipHist.fcSearch(obj,sDistNo,sEquipGbn);


			 //탑재SW 조회 
			 fcSearchInfraMapp();


			 break;
		default:		// COMBO CHANGE 등 이벤트시 

			 sServiceName = "rsy4020-service:searchInfraEquip";
			 sInDataSet   = "";
			 sOutDataSet  = "dsInfraEquip=dsInfraEquip ";

			 //조회조건 : 
			 fcd_AddParam("TPE_1", cboSearchTpe1.Value);
			 fcd_AddParam("TPE_2", cboSearchTpe2.Value);
			 fcd_AddParam("EQUIP_NM", edSearchEquipNm.Text);

			 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

			break;

	}

}


/*-------------------------------------------------------------------------------------------------+
>>  장비 SW 조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchInfraMapp(){
		 sServiceName = "rsy4020-service:searchInfraMapp";
		 sInDataSet   = "";
		 sOutDataSet  = "dsInfraMapp=dsInfraMapp ";

		 fcd_AddParam("DIST_NO", dsInfraEquip.GetColumn(dsInfraEquip.row, "DIST_NO"));


		 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크

	if ( !fnc_DatasetChangeCheck(dsInfraEquip) && !fnc_DatasetChangeCheck(dsInfraMapp) && !tbInfra.tbEquipHist.divEquipHist.fcSaveCntCheck() ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsInfraEquip;
	sMandatoryColID   = "TPE_1|TPE_2|OS_TYP";
	sMandatoryColName = "대분류|중분류|OS타입";
	oGrid             =  grdInfraEquip;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {


	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";



	if ( !fcSaveCheck() ) return;
	if ( !tbInfra.tbEquipHist.divEquipHist.fcSaveCheck(obj) ) return;

	sServiceName = "rsy4020-service:saveInfraEquip";
	sInDataSet   = "dsInfraEquip=dsInfraEquip:u dsInfraMapp=dsInfraMapp:u";

	sOutDataSet  = "dsInfraEquip=dsInfraEquip ";


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	tbInfra.tbEquipHist.divEquipHist.fcSave(obj);
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" : // 장비
			if ( fnc_DatasetChangeCheck(dsInfraEquip) ) {
				//fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
				//return;
			}

			nRow = dsInfraEquip.AddRow(); // Record Add

			dsInfraEquip.SetColumn(nRow,"TPE_1","");
			dsInfraEquip.SetColumn(nRow,"TPE_2","");
			dsInfraEquip.SetColumn(nRow,"INSTALL_LOC","001");
			dsInfraEquip.SetColumn(nRow,"ADOPT_DT",fcd_GetSysdate("YYYYMMDD") );
			dsInfraEquip.SetColumn(nRow,"OS_TYP","");

			grdInfraEquip.SetFocus(); // 입력 첫 항목으로 Focus

			tbInfra.TabIndex = 0;

			tbInfra.tbInfraEquip1.cboTpe1.SetFocus();

			break;


		case "mapBtnRow": // 장비 SW
			if ( fnc_DatasetChangeCheck(dsInfraMapp) ) {
				//fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
				//return;
			}

			nRow = dsInfraMapp.AddRow(); // Record Add

			dsInfraMapp.SetColumn(nRow,"DIST_NO", dsInfraEquip.GetColumn(dsInfraEquip.currow,"DIST_NO") );



			break;

	}
}


/*-------------------------------------------------------------------------------------------------+
>>  행삽입 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInsRow(obj) {

	 switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
			grdInfraEquip.InsertRow(grdInfraEquip.GetCurrow());           // Record Ins

			break;

		case "mapBtnRow" :
			grdInfraEquipSw.InsertRow(grdInfraEquipSw.GetCurrow());           // Record Ins
			break;	


	}

}


/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :	// 장비 삭제 
			if ( !fnc_DeleteCheck(dsInfraEquip, "chk", "1") ) return;

			break;


		case "mapBtnRow" :	//장비 SW 삭제 
			if ( !fnc_DeleteCheck(dsInfraMapp, "chk", "1") ) return;

			break;


	}
}


