/*-------------------------------------------------------------------------------------------------+
>>  Tab 에서 OnChanging 처리
+-------------------------------------------------------------------------------------------------*/
function fcTabOnChanging(obj,nOldIndex,nNewIndex)
{
	switch ( nNewindex ) {

		case 0:	// 기본정보

			break;

		case 2 : //장비이력관리

			if(dsInfraEquip.GetRowCount() <=0 || dsInfraEquip.GetRowType(dsInfraEquip.row) == "insert"){
				fnc_Message(SNIS_RBM_A001, "","저장된 인프라장비","탑재SW");

				tbInfra.TabIndex = nOldIndex;
			}

			break;
		case 3 : //장비이력관리

			if(dsInfraEquip.GetRowCount() <=0 || dsInfraEquip.GetRowType(dsInfraEquip.row) == "insert"){
				fnc_Message(SNIS_RBM_A001, "","저장된 인프라장비","장비이력");

				tbInfra.TabIndex = nOldIndex;
			}

			break;

	}


}

