/*-------------------------------------------------------------------------------------------------+
>>  dataset 에서 OnLoadCompleted 처리
+-------------------------------------------------------------------------------------------------*/

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	switch ( obj.id ) {
		case "dsTpe1" :	//대분류 
			dsTpe1.InsertRow(0);
			dsTpe1.SetColumn(0, "CD_NM", "전체");
			cboSearchTpe1.index = 0;
			tbInfra.tbInfraEquip1.cboTpe1.index = 0;
			break;
		case "dsTpe2" :
			dsTpe2.InsertRow(0);
			dsTpe2.SetColumn(0, "CD_NM", "전체");
			cboSearchTpe2.index = 0;
			tbInfra.tbInfraEquip1.cboTpe2.index = 0;
			break;

		 case "dsTpeG1" : //대분류 
			dsTpeG1.InsertRow(0);
			dsTpeG1.SetColumn(0, "CD", "");
			dsTpeG1.SetColumn(0, "CD_NM", "선택");

			break;
		case "dsTpeG2" :
			dsTpeG2.InsertRow(0);
			dsTpeG2.SetColumn(0, "CD", "");
			dsTpeG2.SetColumn(0, "CD_NM", "선택");
			break;

		case "dsOsTyp" : // OS타입 
			dsOsTyp.InsertRow(0);
			dsOsTyp.SetColumn(0, "CD", "");
			dsOsTyp.SetColumn(0, "CD_NM", "선택");
			break;

	}

}

/*-------------------------------------------------------------------------------------------------+
>>  dataset 에서 OnColumnChanged 처리
+-------------------------------------------------------------------------------------------------*/

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	switch ( obj.id ) {
		case "dsInfraMapp" :  // 장비 SW 

			if(strColumnID =="SW_DIST_NO"){
				var tmpRow = dsInfraSw.FindRow("SW_DIST_NO",varNewValue);

				dsInfraMapp.SetColumn(nRow,"DEV_COMP", dsInfraSw.GetColumn(tmpRow,"DEV_COMP"));
				dsInfraMapp.SetColumn(nRow,"GOOD_NM", dsInfraSw.GetColumn(tmpRow,"GOOD_NM"));
				dsInfraMapp.SetColumn(nRow,"VER_INFO", dsInfraSw.GetColumn(tmpRow,"VER_INFO"));			

			}
			break;

	}

}



