			break;


	}
}




/*-------------------------------------------------------------------------------------------------+
>>  dataset 에서 OnRowPosChanged 처리
+-------------------------------------------------------------------------------------------------*/

function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	switch ( obj.id ) {
		//장비 목록 
		case "dsInfraEquip" :
			 if(nRow>-1){	
				if(dsInfraEquip.GetRowType(dsInfraEquip.row) ==  "insert"){

						dsInfraMapp.ClearData();

						tbInfra.tbEquipHist.divEquipHist.fcInit();

						//신규 행일 경우   초기화 
						sEquipDistNo = "";

				}else if(dsInfraEquip.GetRowType(dsInfraEquip.row) ==  "normal" || dsInfraEquip.GetRowType(dsInfraEquip.row) ==  "update"){

						fcd_AddParam("EQUIP_DIST_NO", dsInfraEquip.GetColumn(nRow,"DIST_NO"));
						fcd_AddParam("CHECK_EQUIP_GBN", "001");

						fcSearch(obj,dsInfraEquip.GetColumn(nRow,"DIST_NO"),"001");

						sEquipDistNo = dsInfraEquip.GetColumn(nRow,"DIST_NO");

				}

				tbInfra.tbInfraEquip1.cboTpe1.SetFocus();
			}
			break;




	}
}



