***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 응용소프트 조회 완료 후
	if ( sServiceName == "rsy4020-service:searchInfraEquip" ) {

		tbInfra.TabIndex = 0;

		dsInfraMapp.ClearData();
		tbInfra.tbEquipHist.divEquipHist.fcInit();

	}


	// 응용소프트웨어 저장 완료 후
	if ( sServiceName == "rsy4020-service:saveInfraEquip" ) {
		fcSearch();
	}


	// 장비이력  저장 완료 후
	if ( sServiceName == "rsy4011-service:saveEquipHistCrd" ) {

	}


	// 장비이력 조회  후
	if ( sServiceName == "rsy4011-service:searchEquipHistCrd" ) {

	}
}



