function fcGrdOnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{
 	var sUrl 	= "";
	var sArgs 		= "";
	var arrReturn   = array(6);

	sUrl = "frm_rsy::RSY4021.xml";
	sArgs = "";	

	arrReturn = Dialog(sUrl, sArgs, 334, 443, "");

	dsInfraMapp.SetColumn(nRow,"SW_DIST_NO",arrReturn[0]);
	dsInfraMapp.SetColumn(nRow,"SW_NM",arrReturn[1]);
	dsInfraMapp.SetColumn(nRow,"BUILD_YEAR",arrReturn[2]);
	dsInfraMapp.SetColumn(nRow,"DEV_COMP",arrReturn[3]);
	dsInfraMapp.SetColumn(nRow,"GOOD_NM",arrReturn[4]);
	dsInfraMapp.SetColumn(nRow,"VER_INFO",arrReturn[5]);

	obj.setCellPos(4);
	obj.setFocus();

}

