#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";


/*********************
*  화면 변수 선언부  *
*********************/

/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){

	//구축년도 설정
	fcSetBuildYear();

    fcSearch();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearch(obj) {	
	//필수 조회 조건 체크

	var sServiceName = "rsy4021-service:searchInfraSw";
    var sInDataSet   = "";
    var sOutDataSet  = "dsInfraSw=dsInfraSw ";

	fcd_AddParam("BUILD_YEAR", cboSearchBuildYear.Value); 
	fcd_AddParam("SW_NM", edSearchSwNm.Text);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


}
/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj) {
	var arrReturn    = array(6);               //결과 반환 할 Array

	if(dsInfraSw.GetRowCount() <=0 ) return;

	arrReturn[0] = dsInfraSw.GetColumn(dsInfraSw.currow, "SW_DIST_NO");   	//SW ID 반환
	arrReturn[1] = dsInfraSw.GetColumn(dsInfraSw.currow, "SW_NM");   		//SW 명 반환
	arrReturn[2] = dsInfraSw.GetColumn(dsInfraSw.currow, "BUILD_YEAR");   	//구축년도  반환
	arrReturn[3] = dsInfraSw.GetColumn(dsInfraSw.currow, "DEV_COMP");   	//개발업체 반환
	arrReturn[4] = dsInfraSw.GetColumn(dsInfraSw.currow, "GOOD_NM");   		//제품명 반환
	arrReturn[5] = dsInfraSw.GetColumn(dsInfraSw.currow, "VER_INFO");   	//버전정보 반환

    Close(arrReturn); 

}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) {
	Close(false);     //Null 반환 후 종료
}



/*-----------------------------------------------+
|  그리드에서 더블 클릭 시 선택과 동일한 작업 !  |
+------------------------------------------------*/
function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY) {

	// 배열에 해당 값을 반환 하고 종료
	fcChoice();

}

/*-----------------------------------------------+
|  그리드에서 Enter Key 시 선택과 동일한 작업 !  |
+------------------------------------------------*/
function fcGrdOnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam) {

	// 엔터키 입력 시 배열에 해당 값을 반환 하고 종료
	if (nChar==13) fcChoice();

}


/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}


function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
   switch ( obj.id ) {
		case "edSearchSwNm" :
			if ( nChar == 13 ) {		
				fcSearch();		

			}
			break;

	}
}

/*-------------------------------------------------------------------------------------------------+
>>  활동년도 설정 
    2011 년~  
+-------------------------------------------------------------------------------------------------*/
function fcSetBuildYear(){

	var sCurYear  = ParseInt(fcd_GetSysdate("YYYY"));


	for(var i= 2011; i <= sCurYear +1; i++){
		nRow = dsBuildYear.AddRow();

		dsBuildYear.SetColumn(nRow,"CD",i);
		dsBuildYear.SetColumn(nRow,"CD_NM",i);		
	}



	dsBuildYear.InsertRow(0);
	dsBuildYear.SetColumn(0, "CD_NM", "전체");
	cboSearchBuildYear.index = 0;

	//cboSearchBuildYear.Value = sCurYear;


}
