/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsWebStat.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	fcd_AddParam("RACE_STR_DT", calStartDate.Value);	
	fcd_AddParam("RACE_END_DT", calEndDate.Value);	

	if (tab0.TabIndex == 0) {
		sServiceName = "rsy4050-service:searchWebStat";
		sOutDataSet  = "dsWebStat=dsWebStat";
	} else if (tab0.TabIndex == 1) {
		sServiceName = "rsy4050-service:searchWebStatTms";
		sOutDataSet  = "dsWebStatTms=dsWebStatTms";
		fcd_AddParam("MEET_CD", rdoMeetCd.Value);	
	} else if (tab0.TabIndex == 2) {
		sServiceName = "rsy4050-service:searchWebStatSum";
		sOutDataSet  = "dsWebStatSum=dsWebStatSum";
	} else if (tab0.TabIndex == 3) {		
		fcSetAmountChart(Tab0.tab4.rptViewer);
		return;
	}

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}



/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsWebStat) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsWebStat;
	sMandatoryColID   = "RACE_DAY";
	sMandatoryColName = "경주일자";
	oGrid             = grdWebStat;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}	
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {    
	if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	sServiceName = "rsy4050-service:saveWebStat";
	sInDataSet   = "dsWebStat=dsWebStat:u ";
	sOutDataSet  = "dsWebStat=dsWebStat ";
	trace(dsWebStat.savexml());

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "divBtnRow" :
		trace(dsWebStat.savexml());
			dsWebStat.AddRow(); // Record Add
			grdWebStat.SetCellPos(1);
			grdWebStat.SetFocus(); // 입력 첫 항목으로 Focus

			break;
	}
}


