/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	var sExcelList = "";
	if (tab0.TabIndex == 0)      sExcelList = "tab0.tab0.grdWebStat:홈페이지 회원 및 접속자 통계";
	else if (tab0.TabIndex == 1) sExcelList = "tab0.tab1.grdWebStatTms:회차별 접속자 통계";
	else                         sExcelList = "tab0.tab2.grdWebStatSum:누적 합계 및 연말 추정치";
	fcd_OpenExcelListExport(sExcelList,1);
}


