/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( sServiceName == "rsy4050-service:searchWebStat" ) {
		SearchCount = dsWebStat.RowCount();
	} else if ( sServiceName == "rsy4050-service:searchWebStatTms" ) {
		SearchCount = dsWebStatTms.RowCount();
	} else if ( sServiceName == "rsy4050-service:searchWebStatSum" ) {
		SearchCount = dsWebStatSum.RowCount();
	}


	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// IP 조회 완료 후
	if ( sServiceName == "rsy4050-service:searchWebStat" ) {
		//trace(dsWebStat.savexml());
		dsWebStat.row = dsWebStat.RowCount() -1;
	} else if ( sServiceName == "rsy4050-service:searchWebStatTms" ) {
		//trace(dsWebStatTms.savexml());
		dsWebStatTms.row = dsWebStatTms.RowCount() -1;
	} else if ( sServiceName == "rsy4050-service:searchWebStatSum" ) {
		if (dsWebStatSum.RowCount() > 1) {
			trace(dsWebStatSum.savexml());
			var nRow = dsWebStatSum.AddRow();
			dsWebStatSum.SetColumn(nRow, "DAY_TERM","증가율(%)");
			dsWebStatSum.SetColumn(nRow, "CVITR_CNT", 			fcIncRate(dsWebStatSum.GetColumn(nRow-2, "CVITR_CNT"), 			dsWebStatSum.GetColumn(nRow-1, "CVITR_CNT")));
			dsWebStatSum.SetColumn(nRow, "CMOBILE_VISTR_CNT", 	fcIncRate(dsWebStatSum.GetColumn(nRow-2, "CMOBILE_VISTR_CNT"), 	dsWebStatSum.GetColumn(nRow-1, "CMOBILE_VISTR_CNT")));
			dsWebStatSum.SetColumn(nRow, "MVISTR_CNT", 			fcIncRate(dsWebStatSum.GetColumn(nRow-2, "MVISTR_CNT"), 			dsWebStatSum.GetColumn(nRow-1, "MVISTR_CNT")));
			dsWebStatSum.SetColumn(nRow, "MMOBILE_VISTR_CNT", 	fcIncRate(dsWebStatSum.GetColumn(nRow-2, "MMOBILE_VISTR_CNT"), 	dsWebStatSum.GetColumn(nRow-1, "MMOBILE_VISTR_CNT")));
			dsWebStatSum.SetColumn(nRow, "RACE_VISTR_CNT", 		fcIncRate(dsWebStatSum.GetColumn(nRow-2, "RACE_VISTR_CNT"), 		dsWebStatSum.GetColumn(nRow-1, "RACE_VISTR_CNT")));
			dsWebStatSum.SetColumn(nRow, "VISTR_SUM", 			fcIncRate(dsWebStatSum.GetColumn(nRow-2, "VISTR_SUM"), 			dsWebStatSum.GetColumn(nRow-1, "VISTR_SUM")));
			dsWebStatSum.SetColumn(nRow, "VISTR_PRDT", 			fcIncRate(dsWebStatSum.GetColumn(nRow-2, "VISTR_PRDT"), 			dsWebStatSum.GetColumn(nRow-1, "VISTR_PRDT")));
		}
	} else if ( sServiceName == "rsy4050-service:searchWebStatWeek" ) {
		Trace(dsWebStatWk.savexml());
		//fcSetAmountChart(Tab0.tab4.rptViewer);
	}


	// IP 저장 완료 후
	if ( sServiceName == "rsy4050-service:saveWebStat" ) {
		fcSearch();		
	}
}

function fcIncRate(valLast, valCur)
{
	var val1 = toNumber(valCur);
	var val2 = toNumber(valLast);

    var val3 = (val1 - val2) / val2;
    Trace("val1:"+val1);
    Trace("val2:"+val1);
    Trace("val3:"+val1);
    return round(val3 * 100);
}


