// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nY)
{	// 해당화면은 기준사이즈보다 Height가 크기때문에 스크롤바 생성발생.그에 따라 그리드사이즈 조정
	var arrObj = array(-1);
	arrObj[0] = "grdWebStat";
	var nHeight = nY-28;

	if( nY <=580){
		grdWebStat.AutoFit = false;
		grdWebStat.height = 479;		
		return;
	}else{			
		grdWebStat.height = nHeight;
		if( nY < 1000) grdWebStat.AutoFit = false;
		else grdWebStat.AutoFit = true;			
	}	

}



