function fcTabOnChanged(obj,nOldIndex,nNewindex)
{
	switch ( nNewindex ) {		
		case 0:	// 일일 현황
		case 2:	// 일일 현황
			fcSearch(this);
			rdoMeetCd.Visible = false;
			if (nNewindex == 0) btnImportWeblog.Visible = true;
			else      			btnImportWeblog.Visible = false;
			break;

		case 1 : //회차별 현황 	
			fcSearch(this);
			rdoMeetCd.Visible = true;
			btnImportWeblog.Visible = false;
			break;	
		case 3 :	// 주별 방문자 추세
			fcSetAmountChart(Tab0.tab4.rptViewer);
			btnImportWeblog.Visible = false;
			break;
	}
}

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch (obj.Id) {
		case "rdoMeetCd":
			fcSearch(this);
			break;
	}
}

function fcSetAmountChart(rptViewer)
{
	// Gateway 방식은 Loadl이나 Test 서버에서 테스트가 불가하며, 반드시 운영환경에서만 테스트 가능
	rptViewer.ServletURL1="http://can.kcycle.or.kr/can/UbiForm";
	rptViewer.ServletURL2="http://can.kcycle.or.kr/can/UbiData";

	rptViewer.datasource = "rbm#jdbc/RBM"; 								// 경주사업관리
    rptViewer.setFileURL(GBL_REPORT_URL);
    rptViewer.setJrfFileDir("/web/waspage/usrbm/report/"); // 절대 경로 주의!        
	rptViewer.setJrfFileName("rsy/rsy4050.jrf");
    rptViewer.FontRevision = true;
	rptViewer.IsDeflater = true;
	rptViewer.SetVariable("isBase64", "true");

	rptViewer.SetArgument("STND_YEAR", Mid(calStartDate.Value,0,4));
    rptViewer.Retrieve();

}

function fcbtnOnClick(obj)
{
	if (obj.id <> "btnImportWeblog") return;
	//if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rsy4050-service:ImportWeblog";
	sOutDataSet  = "dsWebStat=dsWebStat ";	
	sInDataSet   = "dsWebStat=dsWebStat:u dsWebId=dsWebId:a ";

	fcd_AddParam("FILE_PATH", dsFilePath.GetColumn(0, "CD_NM"));	
	fcd_AddParam("RACE_STR_DT", calStartDate.Value);	
	fcd_AddParam("RACE_END_DT", calEndDate.Value);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}
