*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	dsWeekDuty.ClearData();	//보고서 
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCurWeekNo() {


	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	 sServiceName = "rsy4070-service:searchCurWeekNo";
	 sInDataSet   = "";
	 sOutDataSet  = "dsCurWeek=dsCurWeek ";

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	 sServiceName = "rsy4070-service:searchWeekDuty";
	 sInDataSet   = "";
	 sOutDataSet  = "dsWeekDuty=dsWeekDuty ";

	 //조회조건 
     fcd_AddParam("WEEK_NO", sCurWeekNo);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsWeekDuty) && !fnc_DatasetChangeCheck(dsReportMng) && !fnc_DatasetChangeCheck(dsUploadFile)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}


	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rsy4070-service:saveWeekDuty";
	sInDataSet   = "dsWeekDuty=dsWeekDuty:u";
	sOutDataSet  = "dsWeekDuty=dsWeekDuty ";

    fcd_AddParam("WEEK_NO", sCurWeekNo);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}




/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	var sExcelList = "grdWeekDuty:보고서 관리";

	fcd_OpenExcelListExport(sExcelList);
}


